INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680', 2, 'Demane na Demazane', 'assets/topic/asb/2636.png', 'Mutugi Kamundi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_3', 3, NULL, NULL, 'Hapo zamani kuliondokea mvulana mmoja aliyeitwa Demane. Alikuwa na pacha mwenzake aliyeitwa Demazane.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_4', 3, NULL, 'assets/topic/asb/2635.png', 'Wazazi wao walipofariki, Demane na Demazane walienda kuishi na mjomba wao. Mjomba aliwachapa na kuwapatia uji mwepesi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_5', 3, NULL, NULL, 'Kwa sababu hiyo, walitoroka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_6', 3, NULL, 'assets/topic/asb/2636.png', 'Waliamua kuishi pangoni. Waliogopa kwa sababu majitu yaliyoishi karibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_7', 3, NULL, NULL, 'Waliunda mlango wakafunga kiingilio na kuacha mashimo mawili ya kupitisha hewa na mwangaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_8', 3, NULL, 'assets/topic/asb/2637.png', 'Demane akamwambia dadake, "Baki humu pangoni nikatafute chakula. Kamwe usipike nyama. Majitu hupenda sana harufu ya nyama."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_9', 3, NULL, NULL, 'Kila jioni Demane alirudi na nyama na kuimba:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_10', 3, NULL, 'assets/topic/asb/2638.png', 'Demazane alichoka sana kukaa pangoni peke yake. Siku moja alitoka akaenda matembezi. Aliona mto mdogo akaogelea kwa furaha sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_11', 3, NULL, NULL, 'Pia alipanda miti akaendelea kustarehe. Alifurahia uhuru wa kucheza. "Labda hakuna majitu. Pengine mjomba alitutisha tu," aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_12', 3, NULL, 'assets/topic/asb/2639.png', 'Keshoye alipata ukakamavu. Aliuwasha moto pangoni ili kuondoa baridi. Kwa kuogopa majitu, aliufunga mlango kabisa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_13', 3, NULL, NULL, '"Nina hakika hakuna hatari nikichoma nyama kwa moto huu. Chakula cha kuchomwa ni kitamu." Demazane aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_14', 3, NULL, 'assets/topic/asb/2640.png', 'Demane aliporudi nyumbani aliimba kama kawaida:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_15', 3, NULL, NULL, 'Demazane alimwambia kakake, "Nilichoma nyama hii kwenye jua nje ya pango." Kwa vile Demane alikuwa amechoka, hakuuliza maswali zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_16', 3, NULL, 'assets/topic/asb/2641.png', 'Siku iliyofuata Demazane aliwasha moto na akachoma nyama tena. Alpomaliza kuitayarisha, alisikia sauti ikiimba kwenye mlango wa pango:', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_17', 3, NULL, NULL, 'Demazane, Demazane,
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_18', 3, NULL, 'assets/topic/asb/2642.png', 'Baada ya muda mfupi, aliskikia sauti tena ikiimba:
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_19', 3, NULL, NULL, 'Sauti hiyo ilifanana sana na ya kakake kwa hivyo Demazane aliufungua mlango.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_20', 3, NULL, 'assets/topic/asb/2643.png', 'Jitu kubwa lenye nywele mwili mzima lilijaa mlangoni. Likamchukua Demazane haraka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_21', 3, NULL, NULL, 'Demazane aliweza kuliokota jivu la jikoni  na  kulibeba. Alipokuwa akibebwa alimimina jivu lile njiani walikopitia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_22', 3, NULL, 'assets/topic/asb/2646.png', 'Demane aliporudi alipata mlango ukiwa wazi na Demazane hakuwa ndani. Lakini aliona jivu lililomwagwa kulekea msituni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_23', 3, NULL, NULL, 'Alifuata jivu hilo mpaka alipouona moto mbali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_24', 3, NULL, 'assets/topic/asb/2644.png', 'Demane aliona jitu lililojaa nywele limeketi karibu na moto. Mkoba mkubwa ulikuwa karibu nalo. Alisongea karibu nalo akichechemea kama mtu aliyeumia mguu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_25', 3, NULL, NULL, 'Alisema, "Naomba maji ya kunywa. Nimeumia mguu." "Ngoja nikuletee maji lakini usiguse mkoba wangu." Jitu lilisema huku likiweka mkoba ule vizuri ndani ya nyumba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_26', 3, NULL, 'assets/topic/asb/2645.png', 'Jitu lilipoondoka kwenda mtoni, Demane alikata kamba ya mfuko, ukafunguka na dadake akatoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_27', 3, NULL, NULL, 'Demane aliubadilisha mkoba huo na wake uliojaa nyuki. Kisha yeye na dadake wakajificha na kungoja kuona kitakachotokea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_28', 3, NULL, 'assets/topic/asb/2647.png', 'Jitu lilirudi na maji pamoja na watoto wake wawili, msichana na mvulana. Likamwambia msichana,  "Kuna mlo mtamu ndani ya mkoba. Nenda ukaulete!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_29', 3, NULL, NULL, 'Msichana alipofungua mkoba, nyuki walimwuma mkono akalia kwa sauti, "Inauma, inauma." Jitu lilimwambia mvulana aende kumsaidia. Yeye pia aliumwa akalia kwa uchungu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_30', 3, NULL, 'assets/topic/asb/2648.png', '"Msinifanye mjinga," jitu lilisema kwa sauti likajitoma ndani ya nyumba. Liliufunga mlango na kufungua ule mkoba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_31', 3, NULL, NULL, 'Nyuki wale walichomoka kwa wingi na kumwuma kichwani, masikioni na machoni. Lilishindwa kuona likarukaruka na kulia kwa uchungu mwingi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_32', 3, NULL, 'assets/topic/asb/2649.png', 'Jitu lilikimbia kuelekea kwenye bwawa la maji. Lilichopeka kichwa kwenye matope likakwama huko. Liligeuka na kuwa kisiki cha mti uliokatwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('10680_33', 3, NULL, NULL, 'Nyuki walijenga nyumba yao kwenye kisiki hicho. Baadaye, Demane na Demazane walifurahia asali tamu kutoka kwa mzinga huo wa nyuki.', NULL);
