INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612', 2, 'Fahali na Punda', 'assets/topic/asb/15496.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_3', 3, NULL, 'assets/topic/asb/15491.png', 'Zamani aliishi mkulima mmoja maskini aliyekuwa na Fahali mzee. Fahali huyo hakupenda kulima. Alipenda kubaki nyumbani akila nyasi, akinywa maji na kupumzika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_4', 3, NULL, 'assets/topic/asb/15492.png', 'Mkulima huyo vilevile alikuwa na Punda. Punda na Fahali walikuwa marafiki. Mwisho wa siku, walipenda kuongea pamoja. Siku moja, Fahali alisema, "Nimechoka sana. Nimefanya kazi siku nzima. Jembe ni kubwa na zito. Naye mkulima haniruhusu kupumzika."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_5', 3, NULL, 'assets/topic/asb/15493.png', 'Punda alimtazama Fahali akawaza, "Bila shaka Fahali ni mvivu na hapendi kufanya kazi." Kisha akasema, "Unadhani kuwa jembe lako ni zito? Niamini, rafiki yangu, jembe ni jepesi! Mimi leo nimebeba gunia la ngano mgongoni. Nina hakika lilikuwa zito kuliko jembe lako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_6', 3, NULL, 'assets/topic/asb/15494.png', 'Siku iliyofuata, walikutana tena. Fahali akasema,  "Nimekuwa na siku mbaya leo. Shamba la bwana wangu liko mbali na limejaa mawe. Nimefanya kazi mchana kutwa. Sikupumzika hata kidogo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_7', 3, NULL, 'assets/topic/asb/15495.png', '"Eti ulifanya kazi ngumu?" Punda alimwuliza. "Mimi leo nilikwenda sokoni mjini, kilomita nyingi kutoka hapa. Nina hakika nimefanya kazi ngumu kuliko wewe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_8', 3, NULL, 'assets/topic/asb/15496.png', 'Siku iliyofuata, Fahali alirudi nyumbani akiwa amechelewa tena. Alimwambia Punda, "Ah! Rafiki! Leo ilikuwa siku mbaya kwangu." Lakini, Punda hakutaka kumsikiliza Fahali zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_9', 3, NULL, 'assets/topic/asb/15497.png', 'Punda alimwambia, "Wewe huchoka kila wakati na kila siku ni mbaya kwako. Mkulima atakapokuja kesho, lala na ufumbe macho yako useme, ''Moo! Moo!'' Mkulima akifikiri wewe ni mgonjwa, atakuruhusu upumzike." Fahali alilipenda wazo hilo akasema, "Asante mpenzi Punda. Wazo zuri sana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_10', 3, NULL, 'assets/topic/asb/15498.png', 'Asubuhi iliyofuata, Fahali alilala na mkulima alipofika, aliyafumba macho yake akalia, "Moo! Moo!" Mkulima alimtazama akawaza, "Maskini Fahali wangu ni mgonjwa. Lakini, ni lazima nililime shamba langu. Ni nani atakayenisaidia? Haidhuru, kuna Punda! Atalivuta jembe leo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_11', 3, NULL, 'assets/topic/asb/15499.png', 'Mkulima alimpeleka Punda shambani akamfunga jembe na kuanza kumcharaza, "Nenda! Haraka! Vuta!" Alimshurutisha Punda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_12', 3, NULL, 'assets/topic/asb/15500.png', 'Punda alifanya kazi siku nzima. Jioni alikuwa amechoka sana. Alitembea polepole kwenda nyumbani. Fahali alimsubiri na alipomwona,  alisema, "Mpendwa Punda, nilikuwa na siku nzuri leo. Nilikula nyasi, nikanywa maji na nikapumzika chini ya mti mkubwa. Nataka kupumzika tena kesho. Je, nifanyeje? Nipe wazo jingine."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_13', 3, NULL, 'assets/topic/asb/15501.png', 'Punda alimtazama Fahali, akawaza, "Kazi yake ni mbaya kuliko yangu. Sitaki kuifanya tena kesho." Hatimaye, Punda akasema, "Rafiki yangu, kuwa makini. Leo nimemsikia mkulima akimwambia hivi mkewe, ''Fahali wangu ni mchovu kila wakati na sasa ni mgonjwa. Asipopata nafuu kesho, nitamchinja tule nyama.''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15612_14', 3, NULL, 'assets/topic/asb/15502.png', 'Fahali aliogopa sana akalia akisema, "Eti nini? Alisema hivyo? Kesho, nitafanya kazi yangu. Nimepata nafuu sasa na wala si mchovu tena!"', NULL);
