INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613', 2, 'Fisi na Kobe', 'assets/topic/asb/13398.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_3', 3, NULL, 'assets/topic/asb/13395.png', 'Fisi mlafi aliishi katika kijiji cha Mkosi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_4', 3, NULL, 'assets/topic/asb/13397.png', 'Katika kijiji jirani, aliishi Kobe mwoga. Fisi na Kobe walikuwa marafiki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_5', 3, NULL, 'assets/topic/asb/13398.png', 'Fisi alisikia kwamba kulikuwa na nyuki waliotengeneza asali tamu kuliko yote. Nyuki hao walisafiri mbali na mizinga yao mara moja kwa mwaka. "Lazima tuipate ile asali nyuki watakapokuwa safarini," Fisi alimwambia Kobe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_6', 3, NULL, 'assets/topic/asb/13400.png', 'Ingawa Kobe aliogopa kwenda, hakuwa na ujasiri wa kukataa. Asubuhi iliyofuata, Fisi na Kobe walianza safari yao. Fisi alienda kwa kasi huku Kobe akijikokota nyuma kwa woga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_7', 3, NULL, 'assets/topic/asb/13401.png', 'Walipanda milima wakashuka kisha wakavuka mabonde. Waliuvuka mto mkubwa wakaendelea na safari yao wakiwa na nia ya kuifikia ile mizinga ya nyuki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_8', 3, NULL, 'assets/topic/asb/13403.png', 'Hatimaye, waliona mizinga ya nyuki milimani kwenye miti. Fisi alikwenda kwa haraka. Kobe alikuwa tayari kuzirai kutokana na njaa na uchovu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_9', 3, NULL, 'assets/topic/asb/13405.png', 'Fisi alipoufikia mzinga mkubwa zaidi, alimwambia Kobe, "Haraka! Panda juu ya mti uuangushe mzinga chini."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_10', 3, NULL, 'assets/topic/asb/13406.png', 'Kobe aling''ang''ana kuupanda mti. Alipofanya hivyo, aliangalia chini mara kwa mara asije akaanguka. Fisi alikasirishwa na kitendo hicho akaanza kumpigia Kobe kelele.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_11', 3, NULL, 'assets/topic/asb/13407.png', 'Kobe alipougusa mzinga, alisikia sauti ya nyuki. Kumbe walikuwa bado ndani ya mzinga wao! Kobe alishtuka akaanguka chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_12', 3, NULL, 'assets/topic/asb/13408.png', 'Mzinga wa nyuki ulimwangukia Kobe akafunikwa na kufichwa na asali! Fisi alianza kuilamba ile asali. Lakini nyuki hawakufurahia kitendo hicho.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_13', 3, NULL, 'assets/topic/asb/13409.png', 'Nyuki walimuuma Fisi kila sehemu ya mwili akaamua kukimbilia usalama wake. Hata hivyo, nyuki walimfuata wakaendelea kumuuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_14', 3, NULL, 'assets/topic/asb/13410.png', 'Fisi aliteremka kutoka mlimani, akavuka mto na bonde. Nyuki waliendelea kumuuma wakati huo wote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_15', 3, NULL, 'assets/topic/asb/13411.png', 'Kobe naye alikuwa bado chini ya mzinga mkubwa. Hatimaye, kila kitu kilikuwa kimya. Kobe aligundua kwamba mzinga na asali vilikuwa vimekaukia mgongoni kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_16', 3, NULL, 'assets/topic/asb/13412.png', 'Kobe alitambua kuwa hakuhitaji tena kujificha kichakani. Gamba jipya alilopata lingeweza kumkinga na hatari yoyote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_17', 3, NULL, 'assets/topic/asb/13414.png', 'Fisi alikuwa amechoka na alijaa vidonda mwilini. Alizirai kabla kufika nyumbani. Alipoamka,  aligundua kwamba mishale ya nyuki ilisababisha yeye kuwa na madoadoa kwenye ngozi yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15613_18', 3, NULL, NULL, 'Hivi ndivyo kobe alivyopata gamba lake ngumu naye fisi akapata madoadoa kwenye ngozi yake. Urafiki kati ya fisi na kobe uliisha.', NULL);
