INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698', 2, 'Fisi na Kunguru', 'assets/topic/asb/1725.png', 'Pete Mhunzi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_3', 3, NULL, 'assets/topic/asb/1725.png', 'Fisi na Kunguru walikuwa marafiki ingawa walikuwa tofauti. Kunguru aliruka lakini Fisi alitembea tu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_4', 3, NULL, 'assets/topic/asb/1726.png', 'Siku moja Fisi alitaka kumfahamu Kunguru zaidi. Alimwuliza, "Ni kitu gani cheupe kilicho nyuma ya shingo yako?" Kunguru alimjibu Fisi, "Ni nyama nono nimekuwa nikiila huko angani ninaporuka. Nimeila kwa muda mrefu hadi imejibandika shingoni mwangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_5', 3, NULL, NULL, 'Fisi aliposikia juu ya nyama, alidondokwa na mate. Alitamani sana kula nyama hiyo. Ataipataje ikiwa angani naye hakuwa na mabawa ya kuruka na kuifikia? Fisi alimwomba Kunguru, "Tafadhali rafiki yangu, nikopeshe manyoya ili nijitengenezee mabawa. Nina hamu sana ya kuruka kama wewe na kuila ile nyama."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_6', 3, NULL, 'assets/topic/asb/1727.png', 'Kwa ukarimu wake, Kunguru alimpatia Fisi manyoya. Fisi aliyashona mabawa, akayaunganisha na mwili wake akajaribu kuruka. Uzito wake ulimshinda akalazimika kuwa na mpango tofauti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_7', 3, NULL, 'assets/topic/asb/1728.png', 'Fisi alimwambia Kunguru, "Niruhusu niushikilie mkia wako utakaporuka angani." Kunguru alikubali akasema, "Ninajua una hamu ya kuruka. Hebu tufanye hivyo kesho asubuhi." Asubuhi ilipofika, Fisi aliushikilia mkia wa Kunguru, Kunguru aliporuka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_8', 3, NULL, 'assets/topic/asb/1729.png', 'Kunguru aliruka mpaka akachoka kabisa. Fisi alimsihi,  "Tafadhali rafiki yangu, endelea kidogo zaidi!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_9', 3, NULL, NULL, 'Walipokifikia kipande cha kwanza cha wingu kubwa jeupe, Fisi alidhani ni nyama nono. Mara Fisi alisikia sauti. Unyoya mmoja alioushika uling''oka kutoka kwenye mkia wa Kunguru! Akasikia sauti ya pili, ya tatu na ya nne. Kunguru alijihisi mwepesi. Alikuwa ameondokewa na mzigo ''Fisi.'' Kunguru aliimba: Manyoya ya Kunguru jinyonyoe. Manyoya ya Kunguru jinyonyoe. Fisi aliimba kinyume akisema: Manyoya ya Kunguru yashikane yasijinyonyoe. Manyoya ya Kunguru yashikane yasijinyonyoe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_11', 3, NULL, 'assets/topic/asb/1731.png', 'Hatimaye, manyoya yote yaling''oka. Fisi akaelea peke yake angani. Alilirukia wingu jeupe akidhani ni nyama nono ambayo angejishikilia. Lakini,  alipojaribu kuishika ile "nyama," aliyashika mawingu majimaji tu!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_12', 3, NULL, 'assets/topic/asb/1732.png', 'Fisi alianza kuanguka kwa kasi kutoka angani. Alipaza sauti akisema, "Nisaidie! Nisaidie!" Hakuna aliyemsikia. Kunguru alikuwa ametokomea mawinguni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_13', 3, NULL, 'assets/topic/asb/1733.png', 'Fisi alianguka chini kwa kishindo akalala kimya kwa muda. Baadaye aliamka kwa maumivu akipiga mayowe. Mguu wake mmoja ulikuwa umevunjika. Alikuwa na madoadoa mengi mwilini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8698_14', 3, NULL, 'assets/topic/asb/1734.png', 'Fisi hajaweza kuruka. Urafiki baina yake na Kunguru haukuwepo tena.', NULL);
