INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193', 2, 'Fisi, Sungura na beseni', 'assets/topic/asb/19188.png', 'Charles Katiwa Kiema
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_3', 3, NULL, 'assets/topic/asb/19185.png', 'Hapo zamani, Fisi na Sungura walikuwa marafiki sana. Wote walikuwa maskini lakini Sungura alikuwa na punda. Pia alikuwa na beseni ndogo aliyoitumia kumlisha punda wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_4', 3, NULL, 'assets/topic/asb/19186.png', 'Adhuhuri moja, Sungura alitaka kumpatia punda wake maji. Hakuweza kutega maji kutoka kisimani kwa kutumia ile beseni yake ndogo. Basi alienda kumwomba Fisi beseni yake kubwa. Fisi alimpatia kwa sababu ya urafiki wao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_5', 3, NULL, 'assets/topic/asb/19187.png', 'Sungura alipomaliza kumnywesha punda maji, alikwenda nyumbani kwake na beseni ya Fisi. Wiki mbili baadaye, Fisi alihitaji kufua nguo zake lakini alikumbuka kwamba Sungura alikuwa bado na beseni yake. Fisi aliziacha nguo akaenda kwa Sungura kuichukua beseni yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_6', 3, NULL, 'assets/topic/asb/19188.png', 'Sungura alimrudishia Fisi beseni yake kubwa na akampa ndogo vilevile akisema, "Beseni yako ilizaa mtoto. Kwa sababu ya urafiki wetu, nimeamua kukupatia zote mbili." Fisi alifurahi mno akazikubali beseni hizo mbili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_7', 3, NULL, 'assets/topic/asb/19189.png', 'Siku mbili baadaye, Sungura tena alihitaji kumnywesha punda wake maji. Kwa mara nyingine, alimwendea Fisi amsaidie. Fisi aliwaza, "Kwa sababu nitapokea beseni moja zaidi, nitampa tena beseni yangu!" Sungura aliichukua beseni kubwa ya Fisi na kwenda kumnywesha punda wake maji. Baadaye,  aliibeba akaenda nyumbani kwake nayo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_8', 3, NULL, 'assets/topic/asb/19190.png', 'Fisi alisubiri kabla kwenda kuichukua beseni yake kutoka kwa Sungura. Alitaka izae nyingine kama mbeleni! Fisi alipoenda nyumbani kwa Sungura kuichukua beseni yake, Sungura alisema, "Fisi,  rafiki yangu, nilikuwa ninajitayarisha kukuletea ujumbe mbaya. Beseni yako ilikufa siku mbili zilizopita. Si unajua kwamba vitu vyote vilivyo hai lazima vife?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_9', 3, NULL, 'assets/topic/asb/19191.png', 'Fisi hakuweza kuamini aliyoyasikia. Alisema,  "Lazima niwaite wanyama wakubwa waje kuamua kesi hii." Fisi aliwaita wanyama wakubwa kwa mkutano. Ndovu, viboko, vifaru na twiga walifika nyumbani kwa Sungura kuamua kesi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19193_10', 3, NULL, 'assets/topic/asb/19192.png', 'Sungura aliulizwa kueleza kilichotokea. Alisema,  "Mara ya kwanza nilipoomba beseni ya Fisi, ilizaa mtoto nami nilimpatia zote mbili. Nilipoomba mara ya pili, beseni hiyo ilifariki kabla haijazaa. Kwa sababu chochote kilicho hai, vilevile hufa. Hivyo ndivyo ilivyokuwa!" Wanyama wote walikubaliana na Sungura. Fisi aliipoteza beseni yake.', NULL);
