INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911', 2, 'Hadithi kumhusu Wangari Maathai', 'assets/topic/asb/9803.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_3', 3, NULL, 'assets/topic/asb/9803.png', 'Kulikuwa na msichana mdogo aliyefanya kazi shambani na mamake. Waliishi karibu na Mlima Kenya, Afrika Mashariki. Msichana huyo aliitwa Wangari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_4', 3, NULL, 'assets/topic/asb/9804.png', 'Wangari alilima katika bustani ya familia yake. Alipanda mbegu ndogo katika ardhi iliyokuwa na joto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_5', 3, NULL, 'assets/topic/asb/9805.png', 'Alipenda majira ya jioni, wakati jua lilipotua. Giza lilipozidi, Wangari alirudi nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_6', 3, NULL, 'assets/topic/asb/9806.png', 'Wangari alikuwa mwerevu. Alikuwa na hamu ya kwenda shule lakini, wazazi wake walitaka awasaidie nyumbani. Alipofikisha umri wa miaka saba, alikubaliwa akaanza kwenda shule.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_7', 3, NULL, 'assets/topic/asb/9807.png', 'Wangari alipenda kusoma na kujifunza kupitia kila kitabu alichosoma. Alifanya vyema shuleni akaalikwa kwenda Amerika kuendeleza masoma yake. Wangari alichangamshwa sana na nafasi hii. Alipenda kuufahamu ulimwengu zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_8', 3, NULL, 'assets/topic/asb/9808.png', 'Alipokuwa chuoni huko Amerika, Wangari alijifunza mambo mapya. Alisomea mimea na jinsi inavyokua. Alikumbuka vile alivyocheza na ndugu zake. Vilevile, alikumbuka kivuli cha miti maridadi ya misitu ya Kenya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_9', 3, NULL, 'assets/topic/asb/9810.png', 'Alipojifunza, alifahamu kuwa aliwapenda watu wa Kenya. Alitaka wafurahi na wawe huru. Alikumbuka kwao nyumbani Afrika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_10', 3, NULL, 'assets/topic/asb/9811.png', 'Wangari alikamilisha masomo yake akarudi Kenya. Nchi yake ilikuwa imebadilika sana. Watu matajiri walikuwa wameikata miti. Wanawake walikosa kuni. Watu wengi walikuwa maskini. Watoto walikuwa na njaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_11', 3, NULL, 'assets/topic/asb/9812.png', 'Wangari aliwafunza wanawake kupanda miti. Waliiuza miti hiyo na kupata pesa. Walizitumia kwa mahitaji ya familia zao. Walifurahi kwa sababu Wangari aliwasaidia kuwa na nguvu na uwezo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('9911_12', 3, NULL, 'assets/topic/asb/9813.png', 'Muda ulipopita, miti mipya ilikuwa misitu. Mito ilianza tena kupitisha maji. Ujumbe wa Wangari ulienea Afrika nzima. Leo, mamilioni ya miti imesitawi kutokana na juhudi za Wangari.', NULL);
