INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_3', 3, NULL, 'assets/topic/asb/19902.png', 'Chifu aliyeitwa Mfupi na Duara, alilia, "Nina tatizo kubwa sana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_4', 3, NULL, 'assets/topic/asb/19903.png', '"Nimeipoteza pembe yangu inayotumika wakati wa upanzi," Chifu Mfupi na Duara alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_5', 3, NULL, 'assets/topic/asb/19904.png', '"Nitaipata pembe yako. Nitaenda kuitafuta nyumbani kwa Mjomba Mstatili," Jojo alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_6', 3, NULL, 'assets/topic/asb/19905.png', 'Jojo na Mjomba Mstatili walitafuta kila mahali lakini hawakuipata ile pembe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_7', 3, NULL, 'assets/topic/asb/19906.png', '"Nenda uone ikiwa Mama Pembetatu anayo ile pembe," Mjomba Mstatili alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_8', 3, NULL, 'assets/topic/asb/19907.png', 'Jojo alifika nyumbani kwake akanywa maji baridi kabla kuendelea na safari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_9', 3, NULL, 'assets/topic/asb/19908.png', 'Jojo alimtembelea Almasi na kumwuliza ikiwa angeweza kuambatana naye.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_10', 3, NULL, 'assets/topic/asb/19909.png', '"Si mbali lakini nitatayarisha chakula kidogo ili tubebe," Almasi alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_11', 3, NULL, 'assets/topic/asb/19910.png', 'Almasi na Jojo walipumzika na kula chakula chao chini ya mlima wa pembetatu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_12', 3, NULL, 'assets/topic/asb/19911.png', 'Walipokuwa wamepanda nusu ya mlima, walimwona buibui na utando wa ajbu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_13', 3, NULL, 'assets/topic/asb/19912.png', 'Jojo na Almasi walifika  nyumbani kwa Mama Pembetatu', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_14', 3, NULL, 'assets/topic/asb/19913.png', '"Chifu hakuacha pembe yake hapa," Mama Pembetatu alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_15', 3, NULL, 'assets/topic/asb/19914.png', 'Mama Pembetatu alijua mahali wangeipata ile pembe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_16', 3, NULL, 'assets/topic/asb/19915.png', 'Ilikuwa jioni walipowasili nyumbani kwa Chifu Mfupi na Duara.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_17', 3, NULL, 'assets/topic/asb/19916.png', 'Mama Pembetatu alimwelekezea chifu kidole. "Umeifunga pembe yako kichwani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23107_18', 3, NULL, 'assets/topic/asb/19917.png', 'Chifu Mfupi na Duara alimpa Jojo ile pembe kuipuliza. Wote walifurahi tena.', NULL);
