INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135', 2, 'Hakimu asiyekuwa na busara', 'assets/topic/asb/17057.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_3', 3, NULL, 'assets/topic/asb/17050.png', 'Hapo zamani, waliishi majirani wawili marafiki,  Meseret na Demeke. Walikuwa maskini sana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_4', 3, NULL, 'assets/topic/asb/17052.png', 'Demeke alijibu, "Nami lazima nikavinunue vyungu vipya leo sokoni. Vitakuwa vizito mno. Heri ningekuwa na punda vilevile. Kwa sasa, ninazo tu nusu ya pesa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_5', 3, NULL, 'assets/topic/asb/17053.png', 'Meseret alikuwa na wazo. Alisema, "Hebu tumnunue punda pamoja. Wewe utalipa nusu nami nilipe nusu. Nitamtumia kwenda sokoni kwa juma moja nawe umtumie kwa juma lingine."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_6', 3, NULL, 'assets/topic/asb/17054.png', 'Siku moja, babake Meseret alifariki. Meseret alirithi mashamba, miti, ng''ombe na kondoo. Alikuwa tajiri na hakutaka kufanya kazi na Demeke tena. Vilevile,  alitaka nusu yake ya punda.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_7', 3, NULL, 'assets/topic/asb/17055.png', 'Meseret alimwambia Demeke, "Mbwa wangu wanahitaji nyama. Ninataka kumchinja punda wetu wewe uchukuwe nusu yake nami nichukuwe nusu itakayobaki."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_8', 3, NULL, 'assets/topic/asb/17057.png', 'Meseret na Demeke walienda kumwona hakimu alisiyekuwa na busara. Hakuwasikiliza kwa makini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_9', 3, NULL, 'assets/topic/asb/17058.png', 'Meseret alifurahi. Alimchinja punda na kuchukua nusu ya nyama kuwapelekea mbwa wake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_10', 3, NULL, 'assets/topic/asb/17060.png', 'Miezi michache baadaye, Meseret alitaka kujijengea nyumba mpya. Alifikiri, "Nitakiteketeza chumba changu kizee kisha niijenge nyumba mpya nzuri na kubwa." Meseret alivihamisha vitu kutoka chumbani. Demeke alimwuliza, "Unafanya nini?" Meseret alimjibu, "Ninataka kukiteketeza chumba changu ili niijenge nyumba mpya." Demeke alikuwa na wasiwasi, "Lakini, chumba chako ki karibu na changu. Ukikiteketeza, changu pia kitateketea."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_11', 3, NULL, 'assets/topic/asb/17061.png', 'Kwa hasira, Meseret alisema, "Usijaribu kunizuia! Hiki ni chumba changu na nikitaka nitakiteketeza." Demeke alilia, "Wacha! Hebu twende tumwulize hakimu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_12', 3, NULL, 'assets/topic/asb/17062.png', 'Meseret alipokiteketeza chumba chake, upepo uliupeperusha moto hadi kwenye paa la chumba cha Demeke. Punde, chumba chake pia kilianza kuteketea.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_13', 3, NULL, 'assets/topic/asb/17063.png', 'Maskini Demeke alihuzunika sana. Hakuwa na punda wala chumba. Alibaki na shamba peke yake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_14', 3, NULL, 'assets/topic/asb/17064.png', 'Demeke alifanya kazi kwa bidii shambani mwake. Aliyang''oa magugu na kuwafukuza ndege. Wakati wa kuvuna ulipofika, kulikuwa na mazao mazuri ya mbaazi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_15', 3, NULL, 'assets/topic/asb/17066.png', 'Siku moja, wanawe Meseret walipitia katika shamba la Demeke. Waliona mbaazi wakasema, "Mbaazi ni tamu sana!" Walizivamia wakazitoa zote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_16', 3, NULL, 'assets/topic/asb/17068.png', 'Demeke alipowaona wanawe Meseret, aliwaambia, "Nirudishieni mbaazi zangu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_17', 3, NULL, 'assets/topic/asb/17069.png', 'Demeke alimwambia Meseret, "Wanao wamezila mbaazi zangu." Meseret alimjibu, "Nitakupa pesa." "Sitaki pesa zako. Ninataka mbaazi zangu." Demeke alitishia. "Subiri! Hebu twende tumuulize hakimu," Meseret alilia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_18', 3, NULL, 'assets/topic/asb/17070.png', 'Meseret na Demeke walienda kumwona hakimu yule yule. Kama kawaida, hakuwasikiliza kwa makini wala hakufikiria juu ya tukio hilo. "Wanawe Meseret walikula mbaazi zako na ni lazima zirudishwe. Pasua tumbo zao uchukuwe mbaazi zako." Hakimu aliamua.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_19', 3, NULL, 'assets/topic/asb/17071.png', 'Demeke alichukua kisu chake. "Nitakujengea chumba kipya! Naomba msamaha kwa kukiteketeza chumba chako cha kwanza," Meseret alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_20', 3, NULL, 'assets/topic/asb/17073.png', 'Meseret na Demeke walienda kuwaona wazee wa kijiji. Wazee waliongea kwa muda mrefu na mwishowe walimwambia Meseret, "Ulikosea ulipokataa kumlipa Demeke nusu ya punda wake. Ulikosea pia ulipokiteketeza chumba chake, na wanao walipozila mbaazi zake."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_21', 3, NULL, 'assets/topic/asb/17074.png', 'Wazee walimgeukia Demeke wakasema, "Unataka kuwaua wanawe Meseret. Hilo ni kosa pia. Huu ni uamuzi wetu. Lazima Meseret ampatie Demeke nusu ya miti yake yote, mashamba, ng''ombe na kondoo. Hivyo, mtaishi pamoja kwa amani."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('17135_22', 3, NULL, 'assets/topic/asb/17075.png', 'Meseret alimpatia Demeke nusu ya mali yake.
