INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_3', 3, NULL, 'assets/topic/asb/7966.png', 'Siku moja Hamisi na babake walienda sokoni kununua sare ya shule.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_4', 3, NULL, 'assets/topic/asb/7967.png', 'Hamisi akamwona kijana mwingine amevalia shati la kijani kibichi. "Anaonekana nadhifu sana," Hamisi aliwaza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_5', 3, NULL, 'assets/topic/asb/7968.png', '"Shati lile lapendeza sana," Hamisi alimwambia babake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_6', 3, NULL, 'assets/topic/asb/7969.png', 'Hamisi aliendelea kusema, "Tafadhali baba, ninunulie shati la kijani kibichi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_7', 3, NULL, 'assets/topic/asb/7972.png', '"Hamisi, leo nitaweza kununua sare ya shule pekee." Baba alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_8', 3, NULL, 'assets/topic/asb/7973.png', '"Sina pesa za ziada za kukununulia shati kama hilo." Baba akaeleza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_9', 3, NULL, 'assets/topic/asb/7975.png', 'Baada ya kulipia sare, baba alitambua kuwa alikuwa na pesa zilizobaki. Angeweza kumnunulia Hamisi shati la kijani kibichi!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('8040_10', 3, NULL, 'assets/topic/asb/7976.png', 'Hamisi alifurahi sana. Alikimbia kwenda kujichagulia shati la kijani kibichi.', NULL);
