INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386', 2, 'Jambazi', 'assets/topic/asb/17600.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_3', 3, NULL, 'assets/topic/asb/17597.png', 'Hapo zamani za kale, kulikuwa na wafanya biashara waliokwenda kila mahali. Walisafiri wakati wa musimu wa baridi na musimu wa joto. Walivuka milima, majangwa na misitu wakiuza na kununua bidhaa. Wafanya biashara hao waliwaogopa majambazi kwa hivyo walisafiri wakiwa katika vikundi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_4', 3, NULL, 'assets/topic/asb/17598.png', 'Ilitokea kuwa mfanya biashara mmoja hakuweza kupata kikundi cha kusafiri nacho, kwa hivyo, alisafiri peke yake. Alikutana na jambazi akajawa na hofu. Jambazi huyo alimwuliza, "Wewe ni nani?" Alijibu, "Mimi ni mfanya biashara." "Uko peke yako?" "La! Marafiki zangu wako nyuma ingawa sasa niko peke yangu." Alijibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_5', 3, NULL, 'assets/topic/asb/17599.png', 'Wakati huo, mfanya biashara mwingine alikuja kutoka nyuma yao. Alijificha akatazama kwa hofu jambazi yule akimwua mfanya biashara wa kwanza na kumzika. Aliwaza, "Nitaendeleaje na safari yangu? Yaliyompata mwenzangu yatanipata mimi pia." Aliogopa kuvuka akabaki mahali hapo kwa muda. Baadaye, aliwaza, "Siwezi kukaa hapa milele. Lazima niendelee na safari yangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_6', 3, NULL, 'assets/topic/asb/17600.png', 'Alipita mahali ambapo jambazi alikuwa amemwua mfanya biashara wa kwanza. Alifika kijijini akiwa amehuzunika sana. Hakuweza kutabasamu wala kuzungumza na watu. Watu walimwuliza, "Ulikuwa mchangamfu, lakini sasa umehuzunika. Kwa nini?"  "Nimechoka na ninahisi kiu," alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_7', 3, NULL, 'assets/topic/asb/17601.png', 'Alikaa hapo kijijini usiku wote. Siku iliyofuata,  aliendelea na safari yake. Watu walizidi kumwuliza,  "Ni nini kinachokusumbua?" Mfanya biashara alijiuliza, "Je, niwaambie au la?" Mwishowe, aliamua kuwaambia yaliyompata mfanya biashara mwenzake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_8', 3, NULL, 'assets/topic/asb/17602.png', 'Alipomaliza kuwasimulia, wanakijiji walijua kuwa jambazi alikuwa ametoka katika kijiji chao. Walisema, "Ni vibaya sana kitendo kama hiki kutendeka hapa. Tutajaribu kujua aliyeuawa ni nani." Lakini, hawakusema jambo lolote kuhusu jambazi aliyemwua mfanya biashara huyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_9', 3, NULL, 'assets/topic/asb/17603.png', 'Walipokuwa njiani, mfanya biashara yule aliwaambia wanakijiji, "Nilimuona mtu akizika maiti ya mtu mwengine. Sikujua aliyezikwa alikuwa nani wala jambazi alikuwa nani. Sitawahi kurudi mahali hapa tena."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_10', 3, NULL, 'assets/topic/asb/17604.png', 'Mfalme wa kijiji alisikia jinsi wafanya biashara walikuwa wakiuawa na kuzikwa na  majambazi. Alihuzunika. "Je, majambazi wanawezaje kuwafanyia wafanya biashara hivi katika milki yangu?" Alitafuta njia ya kusuluhisha tatizo hilo. "Nitawaalika watu wote katika sherehe kisha nijaribu kuwashika majambazi hao."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_11', 3, NULL, 'assets/topic/asb/17605.png', 'Mfalme alipendwa sana na watu wake. Aliwaandalia sherehe wakulima, makasisi na kila mtu kijijini. Wote walikula, wakanywa na kujiburudisha. Mfalme alisema, "Binti yangu ni mgonjwa. Dawa pekee inayoweza kumponya ni ndimu inayomea karibu na maji yaliyo baridi mbali na wanapoishi binadamu. Kuna yeyote anayeweza kuniletea ndimu hiyo?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_12', 3, NULL, 'assets/topic/asb/17606.png', 'Kila mtu aliwaza, "Je, nitaipata wapi ndimu aina hiyo?" Hawakusema chochote ila jambazi alisimama haraka akasema, "Mheshimiwa, nitakuletea ndimu hiyo, lakini nipe muda wa wiki mbili."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_13', 3, NULL, 'assets/topic/asb/17607.png', 'Jambazi alikwenda moja kwa moja mpaka mahali alipomzika mfanya biashara aliyemwua kwa sababu ndimu aliyotaka mfalme ilimea hapo. Alikimbilia kuipata kabla mtu mwingine kufanya hivyo. Aliziweka ndimu mfukoni kisha akakimbia kumpelekea mfalme.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_14', 3, NULL, 'assets/topic/asb/17608.png', 'Baada ya kusubiri langoni kwa siku kadhaa, alikubaliwa kumwona mfalme. Alisema, "Niko tayari kutimiza mahitaji yako. Nimekuletea ulichokitaka. Tafadhali kipokee." Mfalme alisema, "Nimefurahi umekileta nilichotaka. Ningependa unipe tukiwa mbele ya watu wengi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_15', 3, NULL, 'assets/topic/asb/17609.png', 'Mfalme aliwaita watu wote kutoka kijijini kwenda katika ikulu. Walipokuwa wanakula na kunywa,  alisema, "Mtu huyu ametimiza ahadi yake kama mlivyosikia. Alisema ataleta tunda nililohitaji na amelileta. Ningependa kupokea zawadi hii kubwa mbele yenu nyote."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_16', 3, NULL, 'assets/topic/asb/17610.png', 'Watu walifikiri kuwa jambazi angepata zawadi ya pesa. Walimwonea wivu. Walishangaa jinsi alipata matunda hayo ya pekee. Jambazi aliufungua mfuko wake ili kumpatia mfalme ndimu mbili. Alipozitoa mfukoni, zilikuwa ndimu mbili, lakini alipomkabidhi mfalme, ziligeuka na kuwa mafuvu mawili ya binadamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_17', 3, NULL, 'assets/topic/asb/17611.png', 'Hili lilipofanyika, watu wote walikasirika naye jambazi akapigwa na butwaa. Mfalme alitabasamu. Kwa muda mrefu, alikuwa akitamani kukomesha ujambazi katika ufalme wake. Alitaka vitendo vyao viovu kujulikana kwa kila mtu. Alikuwa akiomba Mola amwonyeshe nani aliyekuwa jambazi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19386_18', 3, NULL, 'assets/topic/asb/17612.png', 'Siku hiyo, maombi yake yalijibiwa. Alijua mtu huyo ndiye aliyekuwa muuaji. Alitoa amri atiwe gerezani.', NULL);
