INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_5', 3, NULL, 'assets/topic/asb/12443.png', 'Walipolala, Sungura aligundua kitu fulani.  Jimbi na familia yake walivificha vichwa vyao walipolala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_6', 3, NULL, 'assets/topic/asb/12449.png', 'Jimbi na familia yake walilala fofofo, lakini Sungura hakulala usiku kucha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_7', 3, NULL, 'assets/topic/asb/12453.png', 'Asubuhi walipoamka, Sungura alitaka kujua siri hiyo. Akauliza, "Ninyi huviweka vichwa vyenu wapi mnapolala?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_8', 3, NULL, 'assets/topic/asb/12454.png', 'Jimbi, akitaka kumchezea shere Sungura, alimjibu, "Rafiki yangu, wakati wa kulala, sisi ndege huvikata vichwa vyetu ili tuweze kulala kwa starehe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12609_9', 3, NULL, 'assets/topic/asb/12455.png', '"Asubuhi tuamkapo, sisi huvirejesha vichwa vyetu mwilini," Jimbi aliendelea kueleza.', NULL);
