INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_3', 3, NULL, 'assets/topic/asb/26434.png', 'Siku moja, mvua kubwa ilikuwa imenyesha. Imani alitazama angani na kwa mara ya kwanza, aliona rangi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_4', 3, NULL, 'assets/topic/asb/26435.png', 'Rangi zile zilipendeza sana. Alidhani kuwa mtu fulani alikuwa amefanya kazi kubwa ya kuipaka anga rangi kama vile babake alivyozipaka nyumba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_5', 3, NULL, 'assets/topic/asb/26436.png', 'Alikimbia kumweleza dadake. "Sarah, tazama! Mtu fulani ametupakia anga rangi na kuirembesha."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_6', 3, NULL, 'assets/topic/asb/26437.png', 'Lakini Sarah alisema, "Usiwe mshenzi! Huo ni upinde. Wewe ni mdogo nami ni mkubwa. Ninajua kwa nini upinde uko angani. Hebu nikuonyeshe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_7', 3, NULL, 'assets/topic/asb/26438.png', 'Alimpeleka Imani katika bustani ya wanyama akasema, "Upinde hautokezei kwa sababu simba wananguruma kwa mara ya kwanza. Wala hautokezei kwa sababu tembo wanazaliwa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_8', 3, NULL, 'assets/topic/asb/26439.png', 'Sarah alimpeleka tena Imani kwenye bustani ya maua akasema, "Upinde hautokezei eti kwa sababu vipepeo wameipoteza rangi yao mawinguni."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_9', 3, NULL, 'assets/topic/asb/26440.png', 'Sarah alimwonyesha Imani kitabu akasema, "Vilevile, upinde hautokezei eti kwa sababu vifaru wanakimbia na kuzitisha rangi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_10', 3, NULL, 'assets/topic/asb/26441.png', '"Ninajua kwa nini pinde hutokezea. Mama alinieleza, nami sasa ninakueleza!" Sarah alimwelekeza Imani kwenye uwanja wa michezo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_11', 3, NULL, 'assets/topic/asb/26442.png', '"Pinde hutokezea angani kwa ajili ya watoto kama mimi na wewe. Siku moja, rangi zilitazama chini kutoka juu na zikafurahia kile zilizoweza kuona."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_12', 3, NULL, 'assets/topic/asb/26443.png', '"Ziliwaona watoto wenye rangi ya maji ya kunde, rangi nyeusi na rangi iliyo katikati. Ziliwaza, ''Ni ajabu ilioje kupendeza kama watoto hawa wanavyopendeza.''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_13', 3, NULL, 'assets/topic/asb/26444.png', '"Rangi hizo zilikuja pamoja angani zikajadiliana ambavyo zingefanya. Ziliamua, ''Tutaweza tu kupendeza tukiwa kikundi.''"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22557_14', 3, NULL, 'assets/topic/asb/26445.png', '"Sasa, huwa zinaungana pamoja na kung''ara namna zinavyoweza. Zina matumaini ya kupendeza kama watoto zinazoona, watoto kama mimi na wewe."', NULL);
