INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_3', 3, NULL, 'assets/topic/asb/1131.png', 'Opio aliishi katika kijiji kisichokuwa na majira ya usiku. Watu walipochoka, walilala na kufanya kazi walipoamka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_4', 3, NULL, 'assets/topic/asb/1132.png', 'Siku moja Opio na mbwa wake walienda kuwinda. Mbwa walipomfukuza paa, Opio aliwafuata. Mwishowe, hakuwaona tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_5', 3, NULL, 'assets/topic/asb/1133.png', 'Opia alikimbia mpaka akachoka. Alikaribishwa na mtu mmoja akapewa pombe kunywa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_6', 3, NULL, 'assets/topic/asb/1134.png', 'Opio alipoamka, hakuweza kuona. Alimlalamikia mwenye nyumba, "Ulinywesha nini? Mbona sioni tena?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_7', 3, NULL, 'assets/topic/asb/1135.png', 'Mwenye nyumba alimwambia Opio, "Sasa ni usiku. Wewe hujui usiku?" Opio aliuliza maswali mengi kuhusu usiku na mchana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_8', 3, NULL, 'assets/topic/asb/1136.png', 'Asubuhi, Opio alielezwa jinsi ya kuupeleka usiku kijijini kwao. Aliambiwa atembea kuelekea kwao bila kutazama nyuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_9', 3, NULL, 'assets/topic/asb/1130.png', 'Usiku huo Opio alisafiri kwenda kijijini kwao akifuatwa na giza. Hakutazama nyuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_10', 3, NULL, 'assets/topic/asb/1137.png', 'Alipofika kijijini kwao, wanakijiji waliogopa wakamwuliza, "Hiki ni kitu gani cheusi kilichokufuata?" Opio aliwaeleza, "Hili ni giza la usiku. Mwangaza huwa mchana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_11', 3, NULL, 'assets/topic/asb/1138.png', 'Opio aliendelea kuwaeleza, "Tutafanya kazi mchana na tutapumzika usiku."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('16587_12', 3, NULL, 'assets/topic/asb/1139.png', 'Na hivyo ndivyo usiku ulivyoingia katika kijiji cha Opio.', NULL);
