INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081', 2, 'Jua, Mwezi, na Maji', 'assets/topic/asb/25567.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_3', 3, NULL, 'assets/topic/asb/25557.png', 'Miaka mingi iliyopita, Jua na Maji walikuwa marafiki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_4', 3, NULL, 'assets/topic/asb/25558.png', 'Jua alipenda kumtembelea Maji mara kwa mara.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_5', 3, NULL, 'assets/topic/asb/25559.png', 'Mwishowe, Jua alimwuliza Maji kwa nini hakuenda kumtembelea.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_6', 3, NULL, 'assets/topic/asb/25560.png', 'Maji alisema, "Ukitaka nikutembelee, jenga uwanja mkubwa. Watu wangu ni wengi mno. Wao huchukuwa nafasi kubwa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_7', 3, NULL, 'assets/topic/asb/25561.png', 'Jua alirudi nyumbani kwa mkewe, Mwezi. Mwezi alimsalimu kwa tabasamu kubwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_8', 3, NULL, 'assets/topic/asb/25562.png', 'Siku iliyofuata, Jua alianza kuandaa uwanja mkubwa ambamo angemtumbuiza rafiki yake, Maji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_9', 3, NULL, 'assets/topic/asb/25563.png', 'Uwanja ulipokuwa tayari, alimwalika Maji awatembelee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_10', 3, NULL, 'assets/topic/asb/25564.png', 'Maji alipofika, alimwuliza Jua iwapo ingekuwa salama yeye kuingia ndani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_11', 3, NULL, 'assets/topic/asb/25565.png', 'Maji akaanza kutiririka ndani ya nyumba!
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_12', 3, NULL, 'assets/topic/asb/25566.png', 'Muda mfupi baadaye, Maji alifika magotini. Alimwuliza Jua iwapo bado ilikuwa salama.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_13', 3, NULL, 'assets/topic/asb/25567.png', 'Maji alifika kiwango cha upeo wa kichwa cha mtu. Alimwambia Jua, "Je, watu wangu zaidi wanaweza kuja?" Jua na Mwezi walijibu, "Ndiyo."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22081_14', 3, NULL, 'assets/topic/asb/25568.png', 'Watu wa Maji waliendelea kuingia hadi wakafika juu ya paa la nyumba. Jua na Mwezi walilazimika kwenda juu angani.
