INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062', 2, 'Kadogo na nduguze', 'assets/topic/asb/19605.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_3', 3, NULL, 'assets/topic/asb/19601.png', 'Hapo zamani za kale, mtu na mkewe walikuwa na watoto wa kiume saba wajinga.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_4', 3, NULL, 'assets/topic/asb/19602.png', 'Mama yao aliomba, "Ee Mungu, mbona watoto wangu wote ni wajinga? Tafadhali, nipe angaa mtoto mmoja mwenye hekima."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_5', 3, NULL, 'assets/topic/asb/19603.png', 'Mwanamke huyo alijifungua mtoto mvulana. Mtoto huyo alikuwa mdogo mno.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_6', 3, NULL, 'assets/topic/asb/19604.png', 'Kadogo hakukua kimo bali aliongezeka umri.  Alikuwa mwerevu sana. Wazazi wake walimpenda lakini, nduguze walimwonea wivu. "Mbona wazazi wetu wanakupenda wewe zaidi yetu sisi?" Walimwuliza Kadogo. "Hebu jitazame! Wewe ni mdogo kama panya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_7', 3, NULL, 'assets/topic/asb/19605.png', 'Kulikuwa na adui aliyeishi karibu na familia ya Kadogo. Siku moja, nduguze Kadogo waliwaza, "Adui yetu ana ng''ombe wengi wanono. Usiku wa leo twendeni tukawaibe fahali wawili. Tutamtumia punda wa baba kubeba nyama zote."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_8', 3, NULL, 'assets/topic/asb/19606.png', 'Usiku huo, nduguze Kadogo walimchukua punda wakaenda kwa adui yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_9', 3, NULL, 'assets/topic/asb/19607.png', 'Walipofika kwa adui, lango lilikuwa limefungwa. Ndugu hao hawakuweza kuingia ndani. Mmoja wao akasema, "Heri tusingemuacha Kadogo nyumbani. Yeye ni mwerevu na angeweza kuingia ndani."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_10', 3, NULL, 'assets/topic/asb/19608.png', 'Kadogo alijitambulisha, "Ndugu zangu, sikubaki nyumbani. Nisubirini hapa nami nitaingia ndani niufungue mlango. Nitawaelekeza fahali wote kwenu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_11', 3, NULL, 'assets/topic/asb/19609.png', 'Kadogo alicheka akasema, "Tazama, kuna mlango unaonitosha mimi."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_12', 3, NULL, 'assets/topic/asb/19610.png', 'Kadogo alipenya kwenye nafais hiyo. Alipanda juu ya lango na kulifungua. Aliwaongoza fahali kutoka nje bila tatizo. Alisema, "Nendeni, fahali wangu wazuri! Nendeni nje!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_13', 3, NULL, 'assets/topic/asb/19611.png', 'Mmoja wa nduguze adui alikuwa amelala katika chumba chake. Mbwa waliokuwa macho walianza kubweka walipomsikia Kadogo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_14', 3, NULL, 'assets/topic/asb/19612.png', 'Kadogo alipomwona, alijifanya kuwa mmoja wa wafanyakazi. "Usiwe na wasiwasi, bwana mkubwa. Fahali wetu wawili walikuwa wamepotea. Sasa ninawarejesha," alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_15', 3, NULL, 'assets/topic/asb/19613.png', 'Kadogo aliwaongoza wale fahali wawili nje. Nduguze walifurahi sana. "Tazama fahali hawa ni wanono! Hebu tuwachinje tule nyama sasa," walisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_16', 3, NULL, 'assets/topic/asb/19614.png', 'Kadogo na nduguze waliwapeleka fahali mbali na nyumba ya adui. Walipofika kwenye mto, wale ndugu saba walitazama maji kisha wakatikisa vichwa vyao.  "Hatuwezi kuuvuka mto huu. Maji haya ni mengi na hatujui kuogelea. Kadogo yuko wapi? Yeye ndiye atakayejua jinsi ya kuvuka ng''ambo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_17', 3, NULL, 'assets/topic/asb/19615.png', 'Kadogo alikuwa ameketi juu ya pembe za fahali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_18', 3, NULL, 'assets/topic/asb/19616.png', 'Mmoja baada ya mwingine, ndugu wale waliushika ukambaa wakavuka mto. Fahali waliogelea mbele yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_19', 3, NULL, 'assets/topic/asb/19617.png', 'Kadogo aliwauliza, "Li wapi fungu langu?" Nduguze walicheka wakasema, "Fungu lako? Wewe ni mdogo sana. Huwezi kula nyama hii. Kadogo, wewe huna nyama." Kadogo alikasirika mno akawaza, "Nitawaadhibu kwa kunitendea hivi." Aliwaambia, "Ikiwa hamtanipa nyama, nipeni kibofu cha fahali mmoja."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_20', 3, NULL, 'assets/topic/asb/19618.png', 'Ndugu hao walimtupia Kadogo kibofu na wakaondoka. Kadogo alikichukua kile kibofu, akakimbia mbele ya nduguze.  Alipanda mti uliokuwa karibu na njia waliyopitia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_21', 3, NULL, 'assets/topic/asb/19619.png', 'Alipokuwa akikipiga kile kibofu, Kadogo alisema, "Tafadhali bwana, sikuwaiba fahali wako. Ni ndugu zangu waliowaiba. Wamewaua fahali wako na kuzigawanya nyama!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22062_22', 3, NULL, 'assets/topic/asb/19620.png', 'Kadogo alishuka kutoka mtini huku akitabasamu. "Nyama yote itakuwa yangu sasa," alisema.', NULL);
