INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468', 2, 'Kate yuko mbinguni', 'assets/topic/asb/26149.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_3', 3, NULL, 'assets/topic/asb/26134.png', 'Kate ana kitu kichwani mwake. Je, watakiondoaje?', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_4', 3, NULL, 'assets/topic/asb/26135.png', 'Kinafanya Kate achoke. Kinamuumiza. Kinamfanya ajihisi mzito.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_5', 3, NULL, 'assets/topic/asb/26136.png', 'Hawezi kula. Hawezi kunywa. Hacheki kama hapo awali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_6', 3, NULL, NULL, 'Jamani, maskini mwanamke huyu! Kichwa kinamuuma. Daktari anapiga kichwa chake picha kutumia mawimbi ya sauti.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_7', 3, NULL, 'assets/topic/asb/26137.png', 'Daktari anauona uvime kichwani mwake. Anasema, "Kuna uvimbe kwenye ubongo wako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_8', 3, NULL, 'assets/topic/asb/26138.png', '"Lazima tuuondoe huu uvimbe mara moja. Lazima tuuondoe sasa hivi!" Daktari anasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_9', 3, NULL, 'assets/topic/asb/26139.png', 'Wanaileta machela.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_10', 3, NULL, 'assets/topic/asb/26140.png', 'Wanatayarisha vifaa vya upasuaji.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_11', 3, NULL, 'assets/topic/asb/26141.png', 'Wanamdunga sindano.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_12', 3, NULL, 'assets/topic/asb/26142.png', 'Wanakata na kukifungua kichwa cha Kate.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_13', 3, NULL, 'assets/topic/asb/26143.png', 'Wanautoa ubongo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_14', 3, NULL, 'assets/topic/asb/26144.png', 'Wanakata na kuundoa ule uvimbe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_15', 3, NULL, 'assets/topic/asb/26145.png', 'Wanaurudisha ubongo kwenye fuvu. Kisha wanashona na kukifunga kichwa tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_16', 3, NULL, 'assets/topic/asb/26146.png', 'Je, Kate anahisi chochote wanachofanya? La! Kate yuko mbinguni. Hahisi. Hali. Hanywi. Kwa saa 24!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_17', 3, NULL, 'assets/topic/asb/26147.png', 'Halafu, Kate anapata uhai!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22468_18', 3, NULL, 'assets/topic/asb/26148.png', 'Sasa anahisi vizuri. Yuko kama mtoto mchanga.', NULL);
