INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127', 2, 'Kato mwakili, na Shida Kubwa', 'assets/topic/asb/2080.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_3', 3, NULL, 'assets/topic/asb/2081.png', 'Katika bustani ndogo jijini, kulikuwa na mti mkubwa. Wanyama wengi waliishi pamoja juu ya mti huo. Paliishi pia jamii ya kichakuro. Viki alikuwa mmoja wao na alipenda kuongea mambo makubwa. Binamuye, Kato kutoka Msitu wa Mbali, alikuwa amekuja kumtembelea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_4', 3, NULL, 'assets/topic/asb/2082.png', 'Jioni moja, wanyama wote walioishi katika ile bustani ndogo walienda kulala. Mara walisikia sauti kubwa ikitoka kwenye uwanja uliokuwa karibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_5', 3, NULL, 'assets/topic/asb/2083.png', 'Kato na vichakuro wengine waliona mashine kubwa ya manjano. Waliona pia wanaume waliovalia kofia ngumu za manjano wakionyesha kwa vidole ile bustani ndogo na nyumba zingine zilizokuwa upande mwingine wa barabara. Walinuia kuzivunja vunja zote kisha wajenge uwanja wa kuegesha magari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_6', 3, NULL, 'assets/topic/asb/2085.png', 'Watu walioishi pale wangehamia nyumba zingine. Lakini hakuna aliyewafikiria juu ya wanyama, ndege na wadudu walioishi katika ile bustani ndogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_7', 3, NULL, 'assets/topic/asb/2086.png', 'Walioziona mashine hiyo kwanza walikuwa familia ya fugo, hasa Bwana Popules, mkubwa kwa umri. Naye alimwelezea Bwana Mopules, ambaye alimwelezea mdogo wao, Pipules Mdogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_8', 3, NULL, 'assets/topic/asb/2087.png', 'Walipokuwa wachanga, waliitwa Pop, Mop na Pip.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_9', 3, NULL, 'assets/topic/asb/2088.png', 'Kulikiwa na vurumai, kukimbia kwingi, kuruka huku na huko bila yeyote kujua la kufanya. Wapi kwa kwenda? Watoto wa vipepeo walisema, "Hebu tutafute majani." Vipepeo wakasema, "Tunataka maua." Panya wadogo walitaka mashimo. Fugo walitaka kujilimia mashimo yao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_10', 3, NULL, 'assets/topic/asb/2089.png', 'Vichakuro, ndege na wadudu walitaka miti, vichaka na nyasi. "Tutapata wapi hii yote? Bila shaka tutakufa," walisema kisha wakalia kwa uchungu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_11', 3, NULL, 'assets/topic/asb/2090.png', 'Mjombake Kato na shangaziye walikuwa kimya sana na vichakuro wote wadogo walisonga karibu na mamao. Kato alisafisha koo na kuanza kuongea huku akiona haya, "Naishi katika Msitu wa Mbali, labda tunaweza kwenda sote huko." Kulikuwa na sauti za mchangamko na mmoja akasema, "Vipi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_12', 3, NULL, 'assets/topic/asb/2091.png', 'Kato alikuna kichwa na kuwaza sana, "Sawa, Kichakuro ya Moja kwa Moja, inaweza kuwapeleka vichakuro na vipepeo, buibui, watoto wa vipepeo na wadudu wote. Ndege wanaweza kuruka au waje nasi. Fugo nao pamoja na panya wadogo na nyoka wanaweza kutumia Fugo ya Moja kwa Moja ipitiayo chini kwa chini. Kila mmoja alifikiria kuwa hilo lilikuwa wazo zuri kabisa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_13', 3, NULL, 'assets/topic/asb/2092.png', 'Bwana Popules aliharakisha kumwuliza dereva wa treni ya moja kwa moja ya chini kwa chini ikiwa angeweza kuwabeba wote hadi Msitu wa Mbali. Dereva alibabaika, akagugumia kabla ya kusema, "Sawa." Sasa ilikuwa zamu ya Bwana Popules kuwa mwungwana. "Marafiki, hamna haja ya kuwa na wasiwasi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('3127_14', 3, NULL, 'assets/topic/asb/2095.png', 'Kwa hivyo, wakati wa saa za asubuhi, Kichakuro Moja kwa Moja na treni aina ya Fugo ya chini kwa chini pamoja na abiria wote, iliendesha safari yake ya kuelekea Msitu wa Mbali ulio na miti, maua,  vidimbwi na ardhi nyororo.', NULL);
