INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544', 2, 'Khalai anapenda mimea', 'assets/topic/asb/13389.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_3', 3, NULL, 'assets/topic/asb/13382.png', 'Khalai ni msichana mwenye umri wa miaka saba. Katika jamii yake, jina lake lina maana sawa na ''aliye mzuri''.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_4', 3, NULL, 'assets/topic/asb/13383.png', 'Khalai anapoamka, anauzungumzia mmea mchanga wa Mchungwa akisema, "Tafadhali mchungwa, kua mkubwa uzae machungwa mengi mabivu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_5', 3, NULL, 'assets/topic/asb/13384.png', 'Khalai anapokuwa njiani kwenda shuleni, anazizungumzia Nyasi. Anasema, "Tafadhali nyasi, zidi kuwa kijani kibichi na wala usinyauke."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_6', 3, NULL, 'assets/topic/asb/13385.png', 'Khalai anapoyapita Maua ya kichakani, anasema,  "Tafadhali maua, endelea kunawiri ili nikutumie kuzirembesha nywele zangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_7', 3, NULL, 'assets/topic/asb/13386.png', 'Anapokuwa shuleni, Khalai anauzungumzia Mti ulioko pale shuleni, "Tafadhali miti, yazae matawi makubwa ili tuweze kusomea chini ya kivuli chako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_8', 3, NULL, 'assets/topic/asb/13387.png', 'Khalai anauzungumzia ua unaoizingira shule yake, "Tafadhali ua, kua uwe mwenye nguvu ili uilinde shule yetu dhidi ya watu wabaya."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_9', 3, NULL, 'assets/topic/asb/13388.png', 'Anaporudi nyumbani alasiri, Khalai anautembelea mti wa mchungwa kutazama ikiwa machungwa yameiva.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('13544_10', 3, NULL, 'assets/topic/asb/13389.png', '"Machungwa bado mabichi," Khalai anashusha pumzi. "Nitakutembelea kesho, mchungwa wee! Labda utanipa chungwa mbivu wakati huo."', NULL);
