INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567', 2, 'Kiangazi na Mto wenye Baraka', 'assets/topic/asb/26514.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_3', 3, NULL, 'assets/topic/asb/26503.png', 'Katika mwaka mmoja, kipindi cha ukame kilikuwa kirefu sana. Kiangazi kikawaathiri wanyama wote. Mito yote ikakauka ila Mto wenye Baraka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_4', 3, NULL, 'assets/topic/asb/26504.png', 'Ngamia alikuwa wa kwanza kuzungumza, "Kama mnavyojua, tuna kiu kikubwa na mito yote imekauka ila Mto wenye Baraka. Lakini mto huo uko mbali sana. Je, tufanyaje?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_5', 3, NULL, 'assets/topic/asb/26505.png', 'Farasi alitoa pendekezo, "Baadhi yetu tunaweza kwenda kwenye Mto huo wenye Baraka. Tutaweza kuyanywa maji na kisha tuwaletee wengine."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_6', 3, NULL, 'assets/topic/asb/26506.png', 'Mbuzi aliruka juu kisha akasema, "Meee, meee! Mimi pia nitaenda kwenye Mto wenye Baraka."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_7', 3, NULL, 'assets/topic/asb/26507.png', 'Haikuwa rahisi kwenda huko. Aliyekuwa nyuma ya wote, alikuwa Kanga. Baada ya kutembea kwa muda mrefu, Kanga alisema, "Rafiki zanguni, siwezi tena kuendelea kwenda kwenye Mto wenye Baraka."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_8', 3, NULL, 'assets/topic/asb/26508.png', 'Waliendelea na safari tena. Muda mfupi baadaye, Jimbi akasema, "Tafadhali, nimechoka. Siwezi tena kuendelea kwenda kwenye Mto wenye Baraka."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_9', 3, NULL, 'assets/topic/asb/26509.png', 'Safari ilipoendelea, Kondoo alichoka kisha akasema, "Nimechoka. Sitaki tena kuendelea kwenda kwenye Mto wenye Baraka."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_10', 3, NULL, 'assets/topic/asb/26510.png', 'Punde tu, Mbuzi naye alichoka na hakutaka kuendelea kuendelea kwenda kwenye Mto wenye Baraka. Farasi aligeuka na kummeza Mbuzi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_11', 3, NULL, 'assets/topic/asb/26511.png', 'Sasa walikuwa wamebaki Ng''ombe na Ngamia peke yao. Waliendelea na safari hadi Ng''ombe alipochoka.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_12', 3, NULL, 'assets/topic/asb/26512.png', 'Ngamia alipobaki peke yake, alijitahidi ili aufikie Mto wenye Baraka. Ni yeye tu ndiye angeweza kuwaokoa wengine.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_13', 3, NULL, 'assets/topic/asb/26513.png', 'Hatimaye, Ngamia aliwasili kwenye Mto wenye Baraka na kulala chini. Kisha akamtapika Ng''ombe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22567_14', 3, NULL, 'assets/topic/asb/26514.png', 'Na hivyo ndivyo wanyama wote walivyofaulu kuyanywa maji kutoka Mto wenye Baraka.', NULL);
