INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_3', 3, NULL, 'assets/topic/asb/26880.png', 'Hapo zamani, kulikuwa na marafiki wawili, Kinywa na Mkono.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_4', 3, NULL, 'assets/topic/asb/26881.png', 'Baada ya muda mfupi, mke wa Kinywa alifariki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_5', 3, NULL, 'assets/topic/asb/26882.png', 'Kinywa aliporudi nyumbani, aligundua kwamba alikuwa amelipoteza panga la Mkono. Alienda kuomba msamaha ila Mkono hakutaka kusikia maelezo yoyote.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_6', 3, NULL, 'assets/topic/asb/26883.png', 'Kinywa hakuwa na la kufanya. Aliamua kurudi alipomzika mkewe ili alitafute panga hilo. Alitafuta kila mahali lakini, hakulipata.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_7', 3, NULL, 'assets/topic/asb/26884.png', 'Kwa huzuni, Kinywa alirudi nyumbani. Alipokaribia kufika, alimwona mbwa akiwa na panga hilo. Wakati huo, aliisikia sauti ya mkewe ikisema, "Huyu ni mtumishi wako."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_8', 3, NULL, 'assets/topic/asb/26885.png', 'Kinywa aliishi na mbwa yule akimsaidia kuwinda. Siku moja aliacha kumwita mbwa na kuanza kumwita Mtumishi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_9', 3, NULL, 'assets/topic/asb/26886.png', 'Baadaye, Mkono alitamani kula nyama, lakini hakuwa na mbwa wa kumwindia. Alikwenda kuomba mbwa wa Kinywa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_10', 3, NULL, 'assets/topic/asb/26887.png', 'Mkono alienda na Mtumishi popote alipotaka. Kila alichomwuliza afanye, alifanya.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_11', 3, NULL, 'assets/topic/asb/26888.png', 'Siku mmoja walipokuwa porini, Mtumishi alipotea. Kinywa alianza kumwita, "Mtumishi! Mtumishi! Uko wapi?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23158_12', 3, NULL, 'assets/topic/asb/26889.png', 'Mkono alirudi nyumbani bila Mtumishi. Alienda kwa rafikiye Kinywa kumpasha habari ya kupotea kwa Mtumishi. Kinywa hakufurahia ujumbe huo.
