INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022', 2, 'Kiumbe cha ajabu', 'assets/topic/asb/19458.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_3', 3, NULL, 'assets/topic/asb/19455.png', 'Familia ya Inzawu ilikuwa yenye furaha. Binti wao wawili walijifunza kutoka kwa mama yao jinsi ya kupika na kutunza bustani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_4', 3, NULL, 'assets/topic/asb/19456.png', 'Kila walipokula chakula cha jioni, waliongea kuhusu waliyotenda siku hiyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_5', 3, NULL, 'assets/topic/asb/19457.png', 'Baba aliwaonya wanawe kutoingia msituni
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_6', 3, NULL, 'assets/topic/asb/19458.png', 'Siku moja, wasichana wale walikuwa wakicheza karibu na mto. Waliuzunguka mti, na ghafla, walikabiliana na kiumbe cha ajabu! Kiumbe hicho kilikuwa na macho mawili, mapua mawili, viganja viwili na mikono miwili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_7', 3, NULL, 'assets/topic/asb/19459.png', 'Wakitetemeka kwa woga, walikimbia kuwaeleza ndugu zao walichokuwa wameona. Hata hivyo, wavulana hao hawakuwaamini, "Ni ujinga wa wasichana tu!" Walisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_8', 3, NULL, 'assets/topic/asb/19460.png', 'Mwishowe, hamu iliwazidi. Wavulana hao waliwafuata dada zao wakaelekea mtoni ili kujionea wenyewe kiumbe hicho cha ajabu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_9', 3, NULL, 'assets/topic/asb/19461.png', 'Walitafuta na kutafuta lakini waliona nyasi
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_10', 3, NULL, 'assets/topic/asb/19462.png', 'Juu ya mti walikiona kiumbe cha ajabu wasichokuwa wameona tangu. Kilikuwa na macho mawili, mapua mawili, viganja viwili na mikono miwili. Kila sehemu ya mwili ilikuwa mbili mbili kinyume na wao waliokuwa na kimoja pekee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_11', 3, NULL, 'assets/topic/asb/19463.png', 'Watoto hao waliogopa, wakakimbia kuwaambia wazazi wao. Muda mfupi baadaye, familia nzima ilikuwa pale mtoni kukichunguza kiumbe hicho cha ajabu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_12', 3, NULL, 'assets/topic/asb/19464.png', '"Msiniumize!" Kiumbe hicho kilisema, huku kikijificha nyuma ya mti. Wakati huo, familia ya Inzawu iliweza kuona mguu mmoja, kiganja kimoja, mkono mmoja na jicho moja pekee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_13', 3, NULL, 'assets/topic/asb/19465.png', '"Mimi ni kama ninyi tu," kiumbe hicho kiliendelea kusema. "Tofauti kati yangu nanyi ni kwamba niliumbwa na mbili mbili ya kila sehemu ya mwili."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('20022_14', 3, NULL, 'assets/topic/asb/19466.png', 'Hivyo ndivyo familia ya Inzawu iligundua kwamba kiumbe hicho hakikuwa cha kuogopwa. Ilikuwa tu hali iliyokuwa tofauti na yao.', NULL);
