INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751', 2, 'Kondoo aliyechoka na maisha ya jijini', 'assets/topic/asb/26535.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_3', 3, NULL, 'assets/topic/asb/26535.png', 'Kondoo aliishi jijini. Alipochoka na maisha ya jiji, aliamua kwenda kichakani.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_4', 3, NULL, 'assets/topic/asb/26536.png', 'Kondoo alimsalimu Fisi, "Hujambo ndugu Fisi? Hujambo ndugu Fisi?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_5', 3, NULL, 'assets/topic/asb/26537.png', 'Kondoo aliposikia hivyo, alianza kukimbia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_6', 3, NULL, 'assets/topic/asb/26538.png', 'Simba alimwambia Fisi, "Kwani hatuwezi hata kupata amani pangoni kwetu? Unaturushia vumbi machoni mwetu!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_7', 3, NULL, 'assets/topic/asb/26539.png', 'Simba alisema, "Wewe Fisi, ni chakula kwangu pia. Je, nilikufukuza? Ulikuja wewe mwenyewe. Usinilaumu baadaye."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_8', 3, NULL, 'assets/topic/asb/26540.png', 'Simba akamwuliza Kondoo, "Mbona ulikuja kichakani?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_9', 3, NULL, 'assets/topic/asb/26541.png', 'Simba akasema, "Zitayarishe dawa za kienyeji unihakikishie kwamba wewe ni mwaguzi kweli." Kondoo akajibu, "Dawa zangu ni ghali mno."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_10', 3, NULL, 'assets/topic/asb/26542.png', 'Kondoo akasema, "Sikio la Fisi ndilo dawa."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_11', 3, NULL, 'assets/topic/asb/26543.png', 'Simba alipolila lile sikio, lilikuwa tamu sana.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_12', 3, NULL, 'assets/topic/asb/26544.png', 'Kondoo akasema, "Ndiyo, inawezekana mfalme wangu." Kwa hivyo, Simba alilikata sikio la Fisi la pili akampatia Kondoo.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24751_13', 3, NULL, 'assets/topic/asb/26545.png', 'Simba akamwuliza Kondoo, "Je, inawezekana kuipata ngozi?" Kondoo akajibu, "Nadhani inawezekana mfalme wangu."
