INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863', 2, 'Kuhesabu Kabichi', 'assets/topic/asb/19328.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_3', 3, NULL, 'assets/topic/asb/19323.png', 'Mama Koki amekuwa akichuma kabichi wiki nzima. Dona, Maya na Duki humsaidia Mama Koki katika bustani yake kila Jumamosi asubuhi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_4', 3, NULL, 'assets/topic/asb/19325.png', 'Teksi iliwafikisha watoto kwenye lango la bustani. Waliliona rundo kubwa la kabichi karibu na gari la Baba Koki. "Salala! Kabichi nyingi kweli!" Maya alishangaa. "Bila shaka kuna kabichi elfu moja hapo!" Duki alicheka. "La! Hasha! Pengine ni mia mbili tu!" Dona alipinga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_5', 3, NULL, 'assets/topic/asb/19326.png', 'Mama Koki alikuwa amesubiri langoni. "Hamjambo, nimefurahi kuwaona!" Aliwasalimu watoto. "Hebu tuanze kazi. Mtahitajika kuzihesabu kabichi na kuweka kumi na mbili katika kila sanduku. Nina masanduku 20. Wawili kati yenu wanaweza kupakia kila mmoja kwenye masanduku 7 na mmoja apakie kwenye masanduku 6."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_6', 3, NULL, 'assets/topic/asb/19327.png', 'Watoto walilitazama lile rundo la kabichi. Wakajadili njia tofauti za kuzihesabu kabichi hizo. "Mimi nitahesabu zangu mbili mbili," Maya akasema. "Ninapenda kuhesabu nne nne. Ni haraka," Duki akajibu. "Mimi nitazihesabu tatu tatu ili niwe tofauti nanyi!" Dona akawajibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_7', 3, NULL, 'assets/topic/asb/19328.png', 'Pindi watoto walijaza masanduku 20. "Kazi nzuri!" Mama Koki aliwapongeza. "Tazama, kuna kabichi zimezalia. Nguruwe wangu wanazipenda, kwa hivyo, hebu tuwagawie," Mama Koki aliamua. "Mlipakia kabichi ngapi katika masanduku? Ni kabichi ngapi zilibaki?" Aliwauliza watoto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_8', 3, NULL, 'assets/topic/asb/19329.png', 'Kazi iliyofuata ilikuwa kuandika bei kwenye kila sanduku kisha kumsaidia Baba Koki kuyapakia kwenye gari. Ilikuwa lazima waweke nusu ya masanduku upande mmoja na nusu nyingine upande wa pili ili uzito ulingane.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_9', 3, NULL, 'assets/topic/asb/19330.png', '"Karibu tukamilishe!" Baba Koki alisema. "Hebu tuone ni masanduku mangapi tunahitaji kuweka kila upande."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_10', 3, NULL, 'assets/topic/asb/19331.png', 'Muda mfupi baadaye, masanduku yote 20 yalikuwa yamepakiwa kwenye gari. Baba Koki alilingurumisha gari na kuondoka kwenda sokoni. Aliwaza, "Nikifaulu kuuza masanduku yote, nitapata pesa za kutosha za kurekebisha zizi la nguruwe na kuwashangaza watoto!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_11', 3, NULL, 'assets/topic/asb/19332.png', 'Nyumbani, Mama Koki na watoto walikuwa wakinyunyizia mimea maji na kukusanya majani. Kufikia saa sita, wote walikuwa wamechoka. Mama Koki aliwaambia, "Ni wakati wa karamu yenu. Je, mnajua nilichowaandalia leo?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_12', 3, NULL, 'assets/topic/asb/19333.png', 'Aliingia ndani kuwaletea alichokuwa amewaandalia. Watoto walisubiri kwa uvumilivu huku wakikisia atakacholeta. Alipowasili, waliona mifuko ya kimori chake ikiwa imejaa!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_13', 3, NULL, 'assets/topic/asb/19334.png', '"Hebu tuone ikiwa mlikisia sawa! Nimewatayarishia nini leo?" Mama Koki aliinamisha mifuko na matofaa yakaanguka mezani. "Nilikisia sawa!" Duki alisema kwa furaha. Mama Koki aliwaambia watoto, "Sasa kumbukeni sharti. Gawanya sawa ili kila mmoja apate haki yake. Hakuna anayepata zaidi wala kidogo kuliko mwingine!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_14', 3, NULL, 'assets/topic/asb/19335.png', 'Kwanza, watoto walihesabu matofaa mawili mawili. Walihesabu: 2, 4, 6, 8, 10, 12, 14, 16, na tofaa moja likabaki. Yote pamoja, yalikuwa matofaa 17.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_15', 3, NULL, 'assets/topic/asb/19336.png', 'Waligawana matofaa sawasawa yakabaki matofaa mawili. Kisha watoto waliyaweka matofaa yao mikobani ili wayapeleke nyumbani kwao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_16', 3, NULL, 'assets/topic/asb/19337.png', '"Hebu tuyakate matofaa mawili yaliyobaki ili tugawane kati yetu," Duki aliwaambia. "Kila mmoja wetu atapata vipande vingapi vya tofaa?" Maya aliuliza. "Ninajua jibu!" Dona alitabasamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21863_17', 3, NULL, 'assets/topic/asb/19338.png', 'Wakati huo, Baba Koki alifika nyumbani kutoka sokoni. Gari lilikuwa tupu naye alikuwa akitabasamu. "Niliuza kabichi zote. Sasa ninaweza kurekebisha zizi la nguruwe na kuwanunulia ule mpira wa miguu mliotaka." Watoto walishangilia, "Yee!"', NULL);
