INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_3', 3, NULL, 'assets/topic/asb/9587.png', 'Hapo zamani, Kuku na Tai walikuwa marafiki. Siku moja, Tai alimkuta Kuku akiwa anapumzika chini ya mti. Alikuwa amesimamia mguu mmoja pekee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_4', 3, NULL, 'assets/topic/asb/9588.png', 'Tai alimwuliza Kuku, "Mguu wako mwingine uko wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_5', 3, NULL, 'assets/topic/asb/9589.png', 'Kuku alimweleza Tai, "Mwenye duka aliukata na badala yake akanipatia vitu hivi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_6', 3, NULL, 'assets/topic/asb/9590.png', 'Tai alimwuliza Kuku ikiwa yeye pia angeweza kufanya  hivyo. Vilevile alitaka mfuko uliojaa vitu. Kuku alikubali.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_7', 3, NULL, 'assets/topic/asb/9591.png', 'Tai alimwendea mwenye duka akitaka aukate mguu wake mmoja na badala yake ampatie vitu vitamu. Mwenye duka alikubali akafanya hivyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_8', 3, NULL, 'assets/topic/asb/9592.png', 'Tai aliruka ruka kwa mguu mmoja akielekea nyumbani. Hata hivyo, alivifurahia vitu alivopewa na mwenye duka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_9', 3, NULL, 'assets/topic/asb/9593.png', 'Tai alipofika nyumbani, alimkuta Kuku akiwa na miguu yake miwili.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('23035_10', 3, NULL, 'assets/topic/asb/9594.png', 'Tai alikasirika sana akaanza kumfukuza Kuku. Lakini hakuweza kumshika. Hiyo ndiyo sababu Tai huwashambulia vifaranga wa Kuku kila mara. Anapomshika mmoja, yeye humla.', NULL);
