INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_5', 3, NULL, 'assets/topic/asb/25731.png', 'Mbweha na Kunguru walienda kutafuta chakula.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_6', 3, NULL, 'assets/topic/asb/25732.png', 'Mbweha alipoona hivyo, alimnyanyua Kunguru.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_7', 3, NULL, 'assets/topic/asb/25733.png', '"Tafadhali, usiniue! Hasa, usinitupe chini ya mwamba," Kunguru alimsihi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_8', 3, NULL, 'assets/topic/asb/25734.png', 'Mbweha alimjibu, "Sitakutupa chini ya mwamba."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_9', 3, NULL, 'assets/topic/asb/25735.png', 'Lakini ulikuwa uongo. Mbweha alinuia kumtupa Kunguru chini ya mwamba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_10', 3, NULL, 'assets/topic/asb/25736.png', 'Mbweha alimptupa Kunguru chini ya mwamba.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_11', 3, NULL, 'assets/topic/asb/25737.png', 'Wakati huo, Kunguru alikuwa amegeuka na kuanza kuruka hewani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_12', 3, NULL, 'assets/topic/asb/25738.png', 'Kunguru alitua mtini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_13', 3, NULL, 'assets/topic/asb/25739.png', '"Niliepuka kwa sababu mimi ni mwerevu," Kunguru alijigamba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22536_14', 3, NULL, 'assets/topic/asb/25740.png', '"Uliepuka kwa sababu nilikuruhusu uepuke," Mbweha alimjibu.', NULL);
