INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_8', 3, NULL, 'assets/topic/asb/17294.png', 'Wiki nyingi zilipita.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_9', 3, NULL, 'assets/topic/asb/17295.png', 'Mbwa alichunga ile pembe.
