INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_11', 3, NULL, 'assets/topic/asb/17297.png', 'Alipoenda kuiba pembe, Nyoka alimuuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_12', 3, NULL, 'assets/topic/asb/17298.png', 'Mume na mkewe walichukua mifugo wa ndugu aliyekufa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_13', 3, NULL, 'assets/topic/asb/17299.png', 'Tai aliwaona matajiri wakibeba pesa na vito vya bei.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_14', 3, NULL, 'assets/topic/asb/17300.png', 'Tai alizichukua pesa na vito.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_15', 3, NULL, 'assets/topic/asb/17301.png', 'Punda alibeba gunia nzito la pesa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_16', 3, NULL, 'assets/topic/asb/17302.png', 'Aliwapelekea mume na mkewe lile gunia la pesa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21837_17', 3, NULL, 'assets/topic/asb/17303.png', 'Mume akasema, "Wanyama wametupa zawadi. Mtu bado."', NULL);
