INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_3', 3, NULL, 'assets/topic/asb/14735.png', 'Mimi na rafiki yangu Kiki hupenda kuchunguza na kuvumbua mambo. Hii ndiyo sababu tunayafahamu mazingira yetu vyema. Siku moja tulipokuwa tukienda nyumbani kutoka shule, tulikubaliana twende mahali tukafanye uchunguzi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_4', 3, NULL, 'assets/topic/asb/14736.png', 'Tulipitia karibu na reli ingawa babake Kiki alikuwa ametuonya dhidi ya kufanya hivyo. Baadaye,  tulimwona mwanamume aliyekuwa amevaa koti refu jeusi. Alikuwa akitufuata polepole.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_5', 3, NULL, 'assets/topic/asb/14737.png', 'Tuliamua kutembea polepole ili tumtazame kwa karibu. Alikuwa mtu wa kuogofya. Ngozi yake ilijaa vipele. Midomo yake ilikuwa midogo na haikuyafunika meno yake ya rangi ya kahawia. Alikuwa na kovu moja kubwa lililofunika nusu ya uso wake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_6', 3, NULL, 'assets/topic/asb/14738.png', 'Moyo wangu ulidunda kwa sauti nikadhani mhuni huyo angeweza kuusikia. Nilimkodolea macho. Hakupendezwa na jinsi nilivyomtazama akanikemea kwa ukali, "Wewe mvulana, unaangalia nini?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_7', 3, NULL, 'assets/topic/asb/14739.png', 'Kwa woga, tuligeuka na kutimua mbio, lakini mimi sikuweza kukimbia kwa kasi. Mhuni huyo akalishika shati langu na kunivuta kwa nguvu. Rafiki yangu Kiki alifanikiwa kutoroka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_8', 3, NULL, 'assets/topic/asb/14740.png', 'Mhuni huyo alininyanyua juu kama karatasi na kunirusha ndani ya gari moja jeupe lililokuwa limeegeshwa hapo uwanjani. Yeye alipanda na kukaa kiti cha mbele. Mwenzake aliyekuwa ndani ya gari, aliniziba macho na kuibana mikono yangu nyuma. Kisha dereva akaliendesha gari hilo kwa kasi ya ambulansi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_9', 3, NULL, 'assets/topic/asb/14741.png', 'Yule jambazi wa pili aliyekuwa ameketi karibu nami,  aliniwekea kitambaa chenye unyevunyevu usoni mwangu. Nilipounusa unyevunyevu huo, nililala. Baadaye nilipoamka, nilijipata nimeketi sakafuni katika chumba kimoja chenye giza totoro. Kilijaa buibui na panya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_10', 3, NULL, 'assets/topic/asb/14742.png', 'Mara mlango wa chumba ukafunguka. Yule mhuni aliyenifunga kitambaa usoni, akaingia akiwa amebeba sahani. "Kula chakula hiki upesi kwa sababu utakwenda safari ndefu," aliniambia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_11', 3, NULL, 'assets/topic/asb/14743.png', 'Nilipolisikia neno ''safari'' niliamua kula ili nipate nguvu za kujiokoa. Mhuni yule aliifungua kamba niliyofungwa nayo. Nilipokuwa nikila, aliketi pembeni na kuvuta sigara. Moshi ulijaa chumbani mwote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_12', 3, NULL, 'assets/topic/asb/14744.png', 'Mlango ulibishwa, mhuni huyo na dereva wakaingia wakiwa wamembeba mvulana. Kiki pia alikuwa ameshikwa! Lo! Tulikuwa tumenaswa katika chumba chenye giza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_13', 3, NULL, 'assets/topic/asb/14745.png', 'Baadaye tulifahamu kuwa yule mhuni mrefu mwenye sura mbaya ndiye alikuwa kiongozi wa genge lile.  Mhuni aliyenipa chakula hakufurahia walivyofanya. Tuliwasikia wakibishana pale nje ya chumba. Kiongozi akasema kwa sauti, "Sijali kama unaijua familia yake. Huwezi kubadili nia yako sasa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_14', 3, NULL, 'assets/topic/asb/14746.png', 'Kutokana na ubishi huo, tulijua kwamba mmoja wao alitufahamu ingawa sikujua ni yupi. Kiki akasema,  "Lazima tupate njia ya kutoroka. Wazazi wetu hawana pesa za kutukomboa." Wahuni hao waliendelea kubishana hadi wakaanza kupigana. Mimi nilimfungua Kiki upesi, tukaufunga mlango na kuanza kutafuta njia ya kutoroka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_15', 3, NULL, 'assets/topic/asb/14747.png', 'Tuliona dirisha lenye mianya myembamba iliyopitisha mwangaza. Tulizivuta mbao za dirisha hadi misumari iliyoziunganisha ikang''oka. Kisha tukaziondoa pakawa na mwanya mkubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_16', 3, NULL, 'assets/topic/asb/14748.png', 'Kiki alikuwa mdogo na mwepesi kuniliko mimi. Tukaamua achapuke kwenda kutafuta usaidizi. Nikamsaidia kupita kwenye kile kidirisha na akaruka chini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_17', 3, NULL, 'assets/topic/asb/14749.png', 'Wahuni waliposikia kishindo cha Kiki, waliugonga mlango na kuufungua. Walikiangalia kile kidirisha  kisha wakaanza kusukumana na kutoka nje. Walikasirika sana, hasa yule mkubwa wao mwenye sura mbaya, walipokosa kujua alikimbilia upande gani. Huku akinipiga, alisema, "Wazazi wako watalipa kwa ajili ya kitendo hiki."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_18', 3, NULL, 'assets/topic/asb/14750.png', 'Wahuni walizipigilia zile mbao na  kukiziba kile kidirisha wakanifungia. Niliwasikia wakibishana tena. Mhuni aliyevuta sigara alitaka niachiliwe. Dereva alihofia kuwa Kiki angekumbuka njia na kuwaleta polisi. Kiongozi naye alitaka tu kupata pesa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_19', 3, NULL, 'assets/topic/asb/14751.png', 'Mara niliisikia sauti ya mtu mzima akisema, "Usiogope. Polisi wameshafika. Lala sakafuni ujifunike kichwa na wala usisonge."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_20', 3, NULL, 'assets/topic/asb/14752.png', 'Nilisikia kelele na milio ya risasi. Sikufahamu kilichokuwa kikiendelea kwani mambo mengi yalitendeka kwa wakati mmoja! Polisi waliingia chumbani na kuwakamata wahuni wale mara moja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_21', 3, NULL, 'assets/topic/asb/14753.png', 'Polisi wa kike aliufungua mlango,  akaingia chumbani na kunifunika kwa blanketi. Aliniambia,  "Rafiki yako alikuwa na bahati kwa sababu alitupata tukishika doria. Mara moja, tuliitisha usaidizi na tukaja kuwatia adabu hawa majambazi!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15934_22', 3, NULL, 'assets/topic/asb/14754.png', 'Wahuni hao walitiwa pingu na kusukumwa ndani ya gari la polisi. Mimi nilingia kwenye gari moja na yule polisi wa kike. Alinipeleka kwa wazazi wangu waliokuwa na wasiwasi.
