INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069', 2, 'Kwa nini buibui ana miguu myembamba', 'assets/topic/asb/1069.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_3', 3, NULL, 'assets/topic/asb/1070.png', 'Kulikuwa na buibui mmoja mvivu aliyeitwa Anansi. Badala ya kujipikia chakula, Anansi alipenda kuwatembelea marafiki zake na kula chakula chao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_4', 3, NULL, 'assets/topic/asb/1071.png', 'Siku moja Anansi alipitia nyumbani kwa Sungura na kunusa mboga zilizokuwa zikipikwa. Alizitamani sana. Sungura alimwambia, "Hazijaiva, lakini unaweza kunisaidia kuosha vyombo tukisubiri ziive."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_5', 3, NULL, 'assets/topic/asb/1072.png', '"Nitakujulishaje zitakapokuwa tayari?" Sungura akamwuliza. Anansi akawaza kidogo halafu akasema, "Nitasokota utando nifunge ncha moja mguuni kwangu na nyingine chunguni. Mboga zitakapoiva, utauvuta utando, nami nitakimbia nije."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_6', 3, NULL, 'assets/topic/asb/1073.png', 'Anansi alimwona Kima na mkewe wakipika maharage. Kima alisema, "Jiunge nasi! Maharage karibu yaive."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_7', 3, NULL, 'assets/topic/asb/1074.png', 'Anansi alipita kwa Nguruwe na kunusia viazi vitamu na asali. Nguruwe alisema, "Ninapika viazi vitamu na asali. Njoo tule."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_8', 3, NULL, 'assets/topic/asb/1075.png', 'Anansi alipofika mtoni, miguu yake yote ilikuwa imefungwa kwa ncha za utando. Anansi akawaza,  "Hili lilikuwa ni wazo zuri sana. Ila najiuliza ni chungu kipi kitakachoiva kwanza?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_9', 3, NULL, 'assets/topic/asb/1076.png', 'Anansi aliendelea kuhisi mvuto baada ya mwingine. Alivutwa pande tatu mara moja. "Ah! Jamani!" Anansi alisema alipovutwa kwa mara ya nne. Kabla hajapumua, akahisi ncha ya tano, ya sita, ya saba,  na ya nane zikivutwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('2069_10', 3, NULL, 'assets/topic/asb/1069.png', 'Anansi alijirusha mtoni kwa haraka. Tando zote zilipochukuliwa na maji, alijivuta taratibu kwa maumivu hadi kando ya mto. Anansi alihisi uchungu, "Ah! Ah!" Labda hili halikuwa wazo zuri kabisa." Siku ile hakupata chakula chochote.
