INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088', 2, 'Kwa nini Nzi humsumbua Ng''ombe', 'assets/topic/asb/19248.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_3', 3, NULL, 'assets/topic/asb/19248.png', 'Je, umewahi kuona kundi la ng''ombe malishoni?
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_4', 3, NULL, 'assets/topic/asb/19249.png', 'Inaaminika kuwa tabia hii ilianza zamani katika nchi ya mbali. Malkia wa nchi hiyo alikuwa tajiri na mkarimu. Siku moja aliamua kuwaandalia wanyama wote karamu. Aliandaa meza kubwa ya nyama, mikate, mboga aina aina na matunda matamu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_5', 3, NULL, 'assets/topic/asb/19250.png', 'Wanyama wailfurahia vyakula wakajilamba midomo. Malkia alisema, "Mnyama mkubwa katika kila meza ndiye atakayegawa chakula katika meza hiyo."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_6', 3, NULL, 'assets/topic/asb/19251.png', 'Kwa vile Nzi alikuwa mdogo, Ng''ombe alijifanya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_7', 3, NULL, 'assets/topic/asb/19252.png', 'Walipomaliza kula mkate, Ng''ombe aliwagawia nyama na mboga. Kila mnyama alipokea sahani kubwa ya chakula. Lakini, kwa vile Nzi alikuwa mdogo, Ng''ombe tena hakumuona. "Zuum! Zuum! Zii! Na mimi, je? Tafadhali nigawie chakula pia." Nzi alilalamika kwa sauti kuliko mara ya kwanza. Ng''ombe alimkasirikia Nzi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_8', 3, NULL, 'assets/topic/asb/19253.png', '"Eboi! Kumbe huna adabu?" Ng''ombe alimwuliza. "Mdudu mdogo kama wewe hustahili kuwa katika karamu ya malkia. Subiri hadi wanyama wote wakubwa wamalize kula." Chakula kilikwisha. Maskini Nzi hakupokea hata chembe. Ng''ombe akaanza kugawa matunda tikiti maji na matofaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_9', 3, NULL, 'assets/topic/asb/19254.png', 'Wanyama wote wakapata chakula isipokuwa Nzi. "Zuum! Zuum! Zii! Na mimi, je? Tafadhali nigawie chakula pia." Nzi alilalamika tena. Ng''ombe alicheka huku akiuma tofaa. "Ninyi mnasikia chochote?" Aliwauliza wanyama wengine. "Mimi sijasikia chochote." Wanyama wote walishiba kupita kiasi isipokuwa Nzi. Tumbo lake lilinguruma kwa njaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_10', 3, NULL, 'assets/topic/asb/19256.png', 'Baada ya chakula, walicheza na kuburudika. Nzi alikuwa amekasirika. Aliamua kurudi nyumbani. Malkia alimwuliza, "Nzi, mbona unaondoka? Hujafurahia karamu?" Nzi aliinama kwa heshima, "Samahani, chakula kilikuwa kitamu. Lakini sikugawiwa hata chembe." Nzi alimweleza malkia namna ilivyokuwa. Malkia alikasirishwa na Ng''ombe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_11', 3, NULL, 'assets/topic/asb/19255.png', 'Malkia akasema, "Kutoka leo, wewe na marafiki zako mtakuwa mkiwasumbua Ng''ombe kila siku tangu asubuhi hadi jioni. Utakizunguka kichwa na kupiga kelele karibu na masikio yake. Hivyo ndivyo atakavyopata funzo. Hatawahi kukudharau tena. Atajua kwamba kwangu mimi, hata mdudu mdogo ni muhimu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22088_12', 3, NULL, 'assets/topic/asb/19257.png', 'Hiyo ndiyo sababu Nzi amekuwa akifanya hadi leo.
