INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_3', 3, NULL, 'assets/topic/asb/6162.png', 'Siku moja, Sungura alikuwa anatembea ukingoni mwa mto.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_4', 3, NULL, 'assets/topic/asb/6163.png', 'Kiboko alikuwa akila majani matamu ya kijani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_5', 3, NULL, 'assets/topic/asb/6145.png', 'Kwa bahati mbaya, Kiboko alimkanyaga Sungura mguuni. Kwa uchungu, Sungura alipiga mayowe  na kumfokea Kiboko akisema, "Wewe Kiboko! Unaukanyaga mguu wangu. Huoni?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_6', 3, NULL, 'assets/topic/asb/6146.png', 'Kiboko alimuomba Sungura msamaha, "Samahani sana rafiki. Sikukusudia. Naomba unisamehe!" Sungura hakumsikiliza. Aliendelea kumfokea Kiboko akisema, "Umefanya makusudi! Siku moja nitalipiza kisasi!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_7', 3, NULL, 'assets/topic/asb/6147.png', 'Siku moja, Sungura alienda kumtafuta Moto na kumwambia, "Naomba umuunguze Kiboko atakapotoka kwenye maji baada ya kula majani. Kanikanyaga!" Moto akajibu, "Hapana shaka, rafiki yangu Sungura. Nitafanya ulivyoniambia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_8', 3, NULL, 'assets/topic/asb/6148.png', 'Kiboko alipokuwa akila majani kando ya mto,  "Whuuush!" Moto akalipuka na kuanza kuyaunguza manyoya ya Kiboko.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_9', 3, NULL, 'assets/topic/asb/6149.png', 'Kiboko alilia kwa uchungu huku akikimbilia majini. Manyoya yake yote yaliungua. Kiboko alipiga mayowe, "Manyoya yangu yameunguzwa! Moto ameyaunguza manyoya yangu yote! Manyoya yangu yameungua! Manyoya yangu mazuri!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15938_10', 3, NULL, 'assets/topic/asb/6170.png', 'Tangu wakati huo, kiboko hatembei mbali na maji kwa kuhofia kuunguzwa na Moto. Sungura alifurahi sana kumuona Kiboko bila manyoya. Akasema,  "Nimelipiza kisasi!"', NULL);
