INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670', 2, 'Likizo kwa Bibi', 'assets/topic/asb/11661.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_3', 3, NULL, 'assets/topic/asb/11657.png', 'Odongo na Apiyo waliishi na baba yao mjini. Kila wakati waliitazamia likizo si tu kwa shule kufungwa, bali pia kumtembelea bibi yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_4', 3, NULL, 'assets/topic/asb/11658.png', 'Odongo na Apiyo walifurahi kwani ulikuwa wakati wa kumtembelea bibi kwa mara nyingine. Usiku huo,  walifunganya mifuko yao tayari kwa safari ndefu ya kwenda kijijini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_5', 3, NULL, 'assets/topic/asb/11659.png', 'Asubuhi iliyofuata, waliondoka mapema kwenye gari la baba yao kuelekea kijijini. Safarini, waliiona milima, wanyama wa pori na mashamba ya majani chai.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_6', 3, NULL, 'assets/topic/asb/11660.png', 'Baada ya muda, walilala kwa sababu ya uchovu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_7', 3, NULL, 'assets/topic/asb/11661.png', 'Walipofika kijijini, baba aliwaamsha. Walimkuta bibi akipumzika kwenye mkeka chini ya mti.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_8', 3, NULL, 'assets/topic/asb/11662.png', 'Bibi aliwakaribisha kwa kucheza na kuimba kwa furaha. Wajukuu wake walifurahi na kumpa zawadi walizomletea kutoka mjini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_9', 3, NULL, 'assets/topic/asb/11663.png', 'Bibi aliwabariki wajukuu wake baada ya kuzifungua zawadi. Alipendezwa na zawadi walizompelekea.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_10', 3, NULL, 'assets/topic/asb/11664.png', 'Baada ya kupmzika, Odongo na Apiyo walitoka nje. Walifurahia kucheza na vipepeo na ndege.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_11', 3, NULL, 'assets/topic/asb/11665.png', 'Walipanda mitini na kuchezea ziwani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_12', 3, NULL, 'assets/topic/asb/11666.png', 'Giza lilipoanza kuingia, walirejea nyumbani. Bibi aliwaandalia chakula, lakini walilala kabla ya kumaliza kukila!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_13', 3, NULL, 'assets/topic/asb/11667.png', 'Siku iliyofuata, baba yao aliondoka na kurudi mjini. Aliwaacha Odongo na Apiyo nyumbani kwa bibi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_14', 3, NULL, 'assets/topic/asb/11668.png', 'Odongo na Apiyo walimsaidia bibi yao kufanya kazi za nyumbani. Walitafuta kuni na kuchota maji. Wakakusanya mayai na kuchuna mboga kutoka kwenye bustani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_15', 3, NULL, 'assets/topic/asb/11669.png', 'Bibi aliwafunza wajukuu wake jinsi ya kupika ugali mwororo walioula kwa kitoweo cha nyama. Pia, aliwaonyesha namna ya kupika wali wa nazi walioula kwa mchuzi wa samaki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_16', 3, NULL, 'assets/topic/asb/11670.png', 'Siku moja subuhi, Odongo aliwapeleka ng''ombe wa bibiye malishoni. Kwa bahati mbaya, waliingia shambani mwa jirani. Jirani alitishia kuwafungia ng''ombe kwake kwa kuila mimea yake. Baada ya siku hiyo, Odongo alikuwa mwangalifu zaidi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_17', 3, NULL, 'assets/topic/asb/11671.png', 'Siku ya soko, Odongo na Apiyo walienda pamoja na bibi. Bibi alikuwa na kibanda cha kuuzia mboga, sukari na sabuni. Apiyo aliwaelezea wateja bei za bidhaa. Odongo naye aliwapakia wateja bidhaa walizonunua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_18', 3, NULL, 'assets/topic/asb/11672.png', 'Kabla ya kurejea nyumbani walimsaidia bibi kuhesabu pesa alizopata siku hiyo. Baadaye, walikunywa chai pamoja.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_19', 3, NULL, 'assets/topic/asb/11673.png', 'Likizo ilipokaribia kuisha, watoto walilazimika kurudi mjini. Bibi alimpatia Odongo kofia naye Apiyo akampatia sweta kama zawadi. Aliwatayarishia chakula watakachokula safarini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_20', 3, NULL, 'assets/topic/asb/11674.png', 'Baba yao alipofika, hawakutaka kuondoka. Walimsihi Bibi kwenda nao mjini. Lakini, alitabasamu na kusema, "Nina kazi nyingi shambani, siwezi kwenda mjini. Nitawasubiri mje hapa kijijini tena."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_21', 3, NULL, 'assets/topic/asb/11675.png', 'Odongo na Apiyo walimkumbatia na kumuaga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('12670_22', 3, NULL, 'assets/topic/asb/11676.png', 'Odongo na Apiyo waliwaambia marafiki zao kuhusu maisha kijijini. Baadhi yao waliamini kuwa maisha ya mjini ni bora. Bali wengine waliamini maisha ya kijijini ni bora zaidi. Lakini wote walikubali kwamba Odongo na Apiyo walipendwa na bibi yao.', NULL);
