INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586', 2, 'Madola mjanja', 'assets/topic/asb/19552.png', 'African Storybook
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_3', 3, NULL, 'assets/topic/asb/19543.png', 'Kulikuwa na tajiri mmoja mjanja aliyeitwa Madola. Aliwaajiri watu waliofanya kazi kwa bidii, lakini walipomaliza, hakuwalipa ada zao.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_4', 3, NULL, 'assets/topic/asb/19544.png', 'Dande alikuwa mvulana mdogo aliyeishi na bibi yake. Alikwenda kwa Madola kuajiriwa ili aweze kumnunulia bibi dawa. Alipata kazi ya kuwachunga mifugo wa Madola.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_5', 3, NULL, 'assets/topic/asb/19545.png', 'Mwisho wa mwezi, Dande alikwenda ili apate malipo yake. Madola alifanya ujanja akamwambia, "Nenda nyumbani kisha urudi kesho nikulipe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_6', 3, NULL, 'assets/topic/asb/19546.png', 'Keshoye, Dande alifika kwa Madola mapema akitarajia kulipwa. Madola alifanya ujeuri tena akamwambia, "Hala! Kwani wewe ni mwanafunzi? Kwa nini umefika hapa mapema hivi? Kwani hii ni shule?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_7', 3, NULL, 'assets/topic/asb/19547.png', 'Madola alimpeleka Dande kando na wafanyakazi wengine akamwambia, "Nitakulipa, lakini, kwanza niletee kibuyu cha asali tamu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_8', 3, NULL, 'assets/topic/asb/19548.png', 'Dande alifikiria, "Ni muhimu sana kuzipata zile hela ili nimnunulie bibi dawa." Alivibeba vibuyu viwili akakimbia msituni kuitafuta asali. Alipouna mzinga, aliviweka vibuyu karibu akasubiri vijae.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_9', 3, NULL, 'assets/topic/asb/19549.png', 'Dande alipokuwa akiwaza jinsi ya kumwadhibu Madola, hakumwona nyoka mdogo mweusi mwenye sumu akiingia katika kibuyu kimoja. Dande aliviziba vile vibuyu ili asali isidondoke kisha akaondoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_10', 3, NULL, 'assets/topic/asb/19550.png', 'Dande alimpelekea Madola kibuyu cha asali akitarajia kulipwa. Madola aliionja asali, akasema,  "Niletee kibuyu cha pili cha asali kama hii ndipo nikulipe." Dande alikimbia kukileta kibuyu cha pili bila kujua ndicho kilichoingiwa yule nyoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_11', 3, NULL, 'assets/topic/asb/19551.png', 'Dande alimpatia Madola kibuyu cha pili akisema, "Ndicho hiki kibuyu cha asali tamu kama ile ya kwanza. Naomba unilipe sasa."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_12', 3, NULL, 'assets/topic/asb/19552.png', 'Madola alikipokea kibuyu lakini kwa mara nyingine,  alimwambia Dande kwa ujanja, "Sikulipi leo. Subiri mwisho wa mwaka." Dande alimtazama Madola kwa hasira kisha akarudi nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_13', 3, NULL, 'assets/topic/asb/19553.png', 'Madola alitaka kuila ile asali kwa pupa. Alipokitia kidole ndani ya kibuyu, aliumwa. Alitoka nje akilia,  "Nimeumwa! Nisaidieni jamani!" Nyoka aliyemuuma alitoroka bila kuonwa na yeyote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19586_14', 3, NULL, 'assets/topic/asb/19554.png', 'Wafanyakazi wote walikimbia wasijue kilichotendeka kwa mwajiri wao. Baadaye, ambulansi ilifika na kumpeleka Madola hospitali.', NULL);
