INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18330_15', 3, NULL, 'assets/topic/asb/12944.png', 'Ingawa aliogopa, Tomaso alimtia moyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('18330_16', 3, NULL, 'assets/topic/asb/12945.png', 'Magozwe alipelekwa katika makao ya watoto.', NULL);
