INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412', 2, 'Maguru apatiana miguu', 'assets/topic/asb/13910.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_3', 3, NULL, 'assets/topic/asb/13904.png', 'Hapo zamani, wanyama hawakuwa na miguu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_4', 3, NULL, 'assets/topic/asb/13905.png', 'Watu pekee ndio waliotembea.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_5', 3, NULL, 'assets/topic/asb/13906.png', 'Siku moja, Maguru aliamua kuwapatia pia wanyama miguu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_6', 3, NULL, 'assets/topic/asb/13907.png', 'Wanyama walifurahi. Sasa wangeweza kutembea na kukimbia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_7', 3, NULL, 'assets/topic/asb/13908.png', 'Ilikuwa vigumu kutambaa. Tumbo zao zilikwaruzwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_8', 3, NULL, 'assets/topic/asb/13909.png', 'Siku ilipofika, wanyama wengi walitambaa kwenda kwa Maguru.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_9', 3, NULL, 'assets/topic/asb/13910.png', 'Kila mnyama alipata miguu minne.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_10', 3, NULL, 'assets/topic/asb/13911.png', 'Walifanana tofauti waliposimama.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_11', 3, NULL, 'assets/topic/asb/13912.png', 'Walizunguka kijijini wakiwaambia watu,  "Hatutatambaa tena."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_12', 3, NULL, 'assets/topic/asb/13913.png', 'Wa mwisho kwenda kwa Maguru alikuwa Jongoo. Maguru alimwuliza, "Kuna mwingine nyuma yako?" Jongoo alijibu, "La! Mimi ndiye wa mwisho."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_13', 3, NULL, 'assets/topic/asb/13914.png', 'Maguru aliwaza, "Ikiwa yeye ndiye wa mwisho, nitaifanyia nini miguu itakayobaki?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_14', 3, NULL, 'assets/topic/asb/13915.png', 'Jongoo aliondoka kwa furaha akiwa na miguu mingi. Aliwaza, "Nitaweza kwenda kwa kasi kuwaliko wote."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_15', 3, NULL, 'assets/topic/asb/13916.png', 'Baada ya Jongoo kuondoka, Nyoka alifika. "Tafadhali,  nipatie nami miguu." Nyoka alimsihi Maguru.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_16', 3, NULL, 'assets/topic/asb/13917.png', '"Nimepatiana miguu yote kwa waliotangulia. Wewe ulikuwa wapi?" Maguru alimwuliza.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_17', 3, NULL, 'assets/topic/asb/13918.png', 'Maguru alitafuta kila mahali lakini hakupata miguu yoyote ya kumpatia Nyoka.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('14412_18', 3, NULL, 'assets/topic/asb/13919.png', 'Maguru alimwambia, "Hakuna miguu iliyobaki."
