INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746', 2, 'Mamba aliye mwilini mwangu', 'assets/topic/asb/6105.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_3', 3, NULL, 'assets/topic/asb/6093.png', 'Ninaitwa Molly. Mimi ni msichana kama wasichana wangine, isipokuwa, nina mamba mwilini mwangu. Wewe huwezi kumwona, lakini ninajua yupo. Jina lake ni Ukosefu-Kinga-Mwilini. Anajificha ndani ya mwili wangu na hawezi kuwaumiza marafiki zangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_4', 3, NULL, 'assets/topic/asb/6094.png', 'Mamba huyu hawezi kuondoka mwilini mwangu na kuingia mwilini mwako. Hata ukikaa karibu nami au ukiushika mkono wangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_5', 3, NULL, 'assets/topic/asb/6092.png', 'Hawezi kuuacha mwili wangu na kuruka ndani ya wako ukila nami chakula cha mchana, au ukilala karibu nami sakafuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_6', 3, NULL, 'assets/topic/asb/6095.png', 'Mamba huyu, Ukosefu-Kinga-Mwilini, amekuwa nami tangu nizaliwe. Anapenda kuwala majeshi wote wa mwilini ambao wangepigana na viini na kunizuia kuwa mgonjwa. Anapowala majeshi wangu kwa wingi, ninaugua sana. Siwezi kwenda shule wala kucheza na marafiki zangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_7', 3, NULL, 'assets/topic/asb/6097.png', 'Ninalazimika kumeza dawa zangu kila siku wakati unaopendekezwa. Nisipofanya hivyo, mamba huyu anaamka kwa hasira na kuwala majeshi wangu tena. Mimi sitaki afanye hivyo. Ndiyo sababu ninamkumbusha bibi kunipa dawa zangu wakati unaofaa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_8', 3, NULL, 'assets/topic/asb/6099.png', 'Nikila mboga na matunda, majeshi walio mwilini mwangu wanapata nguvu. Mamba huyu hawezi kuwashika. Anapolala kwa muda, mimi huwa na nguvu. Hufurahi na kucheza na marafiki zangu tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_9', 3, NULL, 'assets/topic/asb/6139.png', 'Bibi alinipeleka kituo cha afya muda mfupi uliopita. Alipewa dawa na daktari ya kumfanya mamba huyu alale asiweze kuwala majeshi wangu. Mimi huweza kukimbia, kupanda juu na kucheza kama watoto wengine.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_10', 3, NULL, 'assets/topic/asb/6102.png', 'Ninampenda bibi sana. Ni mzuri na mwenye huruma. Ananilisha chakula kizuri na kuhakikisha ninapata vitamini zaidi ili majeshi wangu wawe na nguvu. Nikiugua ananipeleka kituo cha afya mara moja ili nipate matibabu na kupata nafuu. Ninapenda anaponisomea hadithi kabla mimi kwenda kulala.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_11', 3, NULL, 'assets/topic/asb/6103.png', 'Kabla sijalala, mimi huwaza juu ya kusomea katika shule kubwa. Nitaendelea kuzitumia dawa za kumfanya mamba wangu alale. Ningependa kumsaidia bibi na kumsomea hadithi atakapozeeka na kutoweza kuona vizuri. Nikiwa mtu mzima,  ningependa kusoma na kuchangia kutafuta dawa zitakazowafanya mamba wote walio katika miili ya watu, walale milele daima.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_12', 3, NULL, 'assets/topic/asb/6104.png', 'Nitajifunza kuishi na mamba wangu na kufanya alale zaidi nitakavyoweza. Nitakuwa na marafiki wengi na kufurahi kila siku iwezekanavyo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('15746_13', 3, NULL, 'assets/topic/asb/6105.png', 'Bibi, shangazi, wajomba, na marafiki, wananipenda,  ingawa nina mamba mwilini mwangu anayeitwa Ukosefu-Kinga-Mwilini.', NULL);
