INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404', 2, 'Marafiki wawili wadogo', 'assets/topic/asb/18402.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_3', 3, NULL, 'assets/topic/asb/18401.png', 'Hapo zamani za kale,  kulikuwa na Mbuzi Mdogo na Chui Mdogo. Walikuwa marafiki.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_4', 3, NULL, 'assets/topic/asb/18402.png', 'Siku moja walienda kucheza pamoja uwanjani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_5', 3, NULL, 'assets/topic/asb/18403.png', 'Walirudi nyumbani jioni sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_6', 3, NULL, 'assets/topic/asb/18404.png', 'Mbuzi Mdogo alimwambia mamake, "Nilikutana na rafiki mrembo. Ana madoadoa maridadi. Nimefurahi sana. Nitakutana  naye tena kesho."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_7', 3, NULL, 'assets/topic/asb/18405.png', 'Mamake Mbuzi Mdogo alijibu, "Mwanangu, nimefurahi umerudi salama. Huyo si rafiki yetu. Sitakuruhusu ukutane naye tena."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_8', 3, NULL, 'assets/topic/asb/18406.png', 'Wakati huo huo, Chui Mdogo alimwambia mamake,  "Nilikutana na rafiki mrembo. Anapenda kucheza sana. Nimefurahi. Nitakutana naye tena kesho."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_9', 3, NULL, 'assets/topic/asb/18407.png', 'Mamake alijibu, "Aa, maskini mwanangu. Huyo alikuwa mbuzi mdogo mrembo. Nasikitika hukumla. Utakapokutana naye kesho, muue na umle. Usiogope."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_10', 3, NULL, 'assets/topic/asb/18408.png', 'Siku iliyofuata, Chui Mdogo alienda katika kijiji cha Mbuzi Mdogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_11', 3, NULL, 'assets/topic/asb/18409.png', 'Hakumwona Mbuzi Mdogo pale nje. Alikuwa amejificha nyumbani kwa mamake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_12', 3, NULL, 'assets/topic/asb/18410.png', 'Chui Mdogo alipaza sauti, "Twende uwanjani tukacheze."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_13', 3, NULL, 'assets/topic/asb/18411.png', 'Mbuzi Mdogo alijibu, "Sisi si marafiki tena. Leo siji. Nimemsikiza mamangu. Mtoto mzuri humsikiza mamake."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19404_14', 3, NULL, 'assets/topic/asb/18412.png', 'Chui Mdogo alihuzunika. Akarudi kwa mamake.', NULL);
