INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462', 2, 'Masanduku matatu yenye mali', 'assets/topic/asb/26153.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_3', 3, NULL, 'assets/topic/asb/26150.png', 'Kulikuwa na mzee mmoja aliyeitwa Hagos. Aliishi na wanawe watatu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_4', 3, NULL, 'assets/topic/asb/26151.png', 'Hagos alikwenda kwa Haile, jirani yake, na kumwambia, "Ninataka wanangu wawe na mali.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_5', 3, NULL, 'assets/topic/asb/26152.png', 'Baada ya Hagos kufariki, Haile aliwaita pamoja wale ndugu watatu akawaeleza, "Kabla baba yenu kufariki, alinipatia haya masanduku matatu. Kila sanduku lina jina. Naomba kila mmoja alichukue sanduku lililo na jina lake."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_6', 3, NULL, 'assets/topic/asb/26153.png', 'Ndugu wale walichukua kila mmoja sanduku lake kisha wakayafungua.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_7', 3, NULL, 'assets/topic/asb/26154.png', 'Aliyepata dhahabu alifurahi sana. Waliopata udongo na samadi, hawakufurahi. Walianza kugombana na yule aliyepata dhahabu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_8', 3, NULL, 'assets/topic/asb/26155.png', 'Haile aliwaambia, "Ninyi ni ndugu na hamstahili kugombana. Hebu niwapeleke kwa mzee mwenye busara awashauri."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_9', 3, NULL, 'assets/topic/asb/26156.png', 'Haile aliwapeleka ndugu wale watatu kwa mzee mwenye busara. "Habari za asubuhi. Tumekuja kwako kutafuta ushauri kuhusu urithi," Haile alimweleza yule mzee.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_10', 3, NULL, 'assets/topic/asb/26157.png', 'Haile alimwambia yule mzee mwenye busara kuhusu yale masanduku matatu ambayo Hagos aliwaachia wanawe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_11', 3, NULL, 'assets/topic/asb/26158.png', 'Yule mzee mwenye busara alisizitiza, "Baba yenu alikuwa na sababu ya kuwapatia sanduku ambalo kila mmoja wenu alipata."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_12', 3, NULL, 'assets/topic/asb/26159.png', 'Aliendelea kuwaeleza, "Wewe ulipata dhahabu kwa sababu babako alitaka uwe mfanya biashara. Nawe ulipata udongo kwa sababu babako alitaka uwe mkulima."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_13', 3, NULL, 'assets/topic/asb/26160.png', '"Wewe ulipata samadi kwa sababu babako alitaka uwe mchungaji. Baba yenu alitaka mfanye kazi yenu kulingana na vipaji vyenu," mzee mwenye busara alikamilisha maelezo yake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22462_14', 3, NULL, 'assets/topic/asb/26161.png', 'Baada ya kusikiliza ushauri wa yule mzee mwenye busara, wale ndugu watatu walikubaliana naye. Kila mmoja wao aliifanya kazi yake tofauti na wakaishi kwa furaha.', NULL);
