INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582', 2, 'Maswali aliyouliza Kariza', 'assets/topic/asb/25965.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_3', 3, NULL, 'assets/topic/asb/25964.png', 'Kariza alipenda kuuliza maswali. Aliyapata mazoea haya kutoka kwa wazazi wake waliokuwa wakimwambia, "Usipouliza maswali ukiwa mdogo, utakapokua mtu mzima, utakuwa mjinga!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_4', 3, NULL, 'assets/topic/asb/25965.png', 'Siku moja, Kariza alimwuliza mwalimu wake, "Kwa nini wazazi wetu wanatueleza kila wakati tuwe tukinawa mikono yetu kabla ya kula, hata kama mikono yetu huonekana kuwa misafi?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_5', 3, NULL, 'assets/topic/asb/25966.png', 'Mwalimu alijibu, "Hilo ni swali nzuri, Kariza. Mikono yetu hata inapoonekana kuwa misafi, inaweza kuwa na viini." Mwalimu aliwaeleza, "Viini husababisha maradhi. Hatuwezi kuviona viini hivyo kwa macho yetu. Tunahitaji kifaa kilicho na nguvu zaidi ili tuvione."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_6', 3, NULL, 'assets/topic/asb/25967.png', 'Mwalimu aliichukua darubini kutoka kabatini. "Darubini ni kifaa tunachotumia kuviona vitu vilivyo vidogo sana ambavyo hatuwezi kuviona kwa macho yetu," alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_7', 3, NULL, 'assets/topic/asb/25968.png', 'Mwalimu alikiweka kile kioo juu ya darubini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_8', 3, NULL, 'assets/topic/asb/25969.png', '"Kuna viini kila mahali. Vinapatikana kwenye vitu tunavyogusa, darasani kwetu, uwanjani na hata nyumbani. Viini hivi vinaweza kutufanya tuwe wagonjwa," mwalimu aliwaonya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_9', 3, NULL, 'assets/topic/asb/25970.png', 'Mwalimu aliendelea, "Ili kuviangamiza viini hivi, lazima tuioshe mikono yetu kwa maji safi na sabuni, hasa kabla ya kula. Pia, tunapokuwa wagonjwa, lazima tuioshe ili tusivieneze viini."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_10', 3, NULL, 'assets/topic/asb/25971.png', 'Kariza alipofika nyumbani, alimkuta babake akiwa anatengeneza kiti cha aina ya pekee.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_11', 3, NULL, 'assets/topic/asb/25972.png', 'Kariza alishangaa akasema, "Ndiyo! Mwalimu wetu ametuambia kuhusu kifaa hiki, lakini wengi wetu hatujui jinsi ya kukitumia. Kinatumikaje?"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_12', 3, NULL, 'assets/topic/asb/25973.png', '"Kwanza kabisa, kikanyage kipande hiki cha mti kilichoko sakafuni," baba akasema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_13', 3, NULL, 'assets/topic/asb/25974.png', '"Unapofanya hivyo, mtungi wa maji utainama na kuyamwaga maji mikononi kwako. Kumbuka kuitumia sabuni kunawa," baba akamwambia Kariza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22582_14', 3, NULL, 'assets/topic/asb/25975.png', 'Kariza alifurahi akasema, "Ningejuaje jambo hili nisingeuliza maswali? Ni kweli kwamba maswali humwelekeza mtu kupata maarifa."', NULL);
