INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403', 2, 'Mawaidha kutoka kwa Baba', 'assets/topic/asb/17114.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_3', 3, NULL, 'assets/topic/asb/17103.png', 'Hapo zamani za kale, kulikuwa na mwanamume aliyekuwa na watoto wawili wa kiume.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_4', 3, NULL, 'assets/topic/asb/17104.png', 'Siku moja, aliwaita akawaambia, "Wanangu, mimi ninakaribia kufa. Ningependa kuwaona mkiwa nyumbani mwenu kabla ya kifo changu. Ninawapa mwezi mmoja ili muweze kulishughulikia jambo hili. Rudini hapa baada ya mwezi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_5', 3, NULL, 'assets/topic/asb/17105.png', 'Wanawe hao wawili walitoka kwa kasi. Mwana wa kwanza alikwenda akakata miti akaanza kujijengea nyumba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_6', 3, NULL, 'assets/topic/asb/17106.png', 'Naye mwana wa pili alikwenda akaanza kujenga uhusiano maalum na watu kutoka familia mbali mbali. Alichukuliwa na watu tofauti kama mtoto wao wa kupanga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_7', 3, NULL, 'assets/topic/asb/17107.png', 'Baada ya mwezi mmoja, ndugu hao wawili walirudi kumwona baba yao. Baba yao alisema, "Je, mmeweza kujenga nyumba zenu?" Na wote walimjibu, "Ndiyo."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_8', 3, NULL, 'assets/topic/asb/17108.png', 'Baba alikwenda na mwanawe wa kwanza. Aliona kwamba mwanawe alikuwa amejenga nyumba nyingi. Alitembea nje ya kila nyumba, akiuliza,  "Je, kuna mtu yeyote ndani ya nyumba hii?" Naye mwanawe alimjibu, "Hapana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_9', 3, NULL, 'assets/topic/asb/17109.png', 'Waliitembelea kila nyumba na kila wakati baba alimwuliza mwanawe, "Je, kuna mtu yeyote kwenye nyumba hii?" Kila mara mwanawe alimjibu, "Hapana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_10', 3, NULL, 'assets/topic/asb/17110.png', 'Hatimaye, baba alihisi njaa kwa sababu hakumpata mtu yeyote wa kumpa chakula. Alisema, "Sawa, twende nyumbani." Waliondoka wakaenda nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_11', 3, NULL, 'assets/topic/asb/17111.png', 'Kaka wa pili aliwapeleka kwenye familia yake ya kwanza ya kupanga. Aliwajulisha kwa baba yake na kusema, "Hawa hapa ni baba na kaka zangu." Familia hii iliwapokea kwa furaha nyingi. Walichinja kondoo wakawatayarishia sherehe kubwa sana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_12', 3, NULL, 'assets/topic/asb/17112.png', 'Kisha walikwenda kwa familia ya pili aliyokuwa amepanga. Aliwajulisha pia kama baba na kaka zake. Waliwafanyia sherehe nyingine kubwa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_13', 3, NULL, 'assets/topic/asb/17113.png', 'Walikaribishwa katika kila familia ambako ndugu wa pili alikuwa amechukuliwa kama mwanao. Walikula wakashiba kisha wakaondoka wakarudi nyumbani.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19403_14', 3, NULL, 'assets/topic/asb/17114.png', 'Baba alisema, "Hivi ndivyo nilivyokusudia nilipowaambia mjitengenezee nyumba. Nyumbani hakumaanishi kuwa na nyumba nyingi au nyumba za bei ghali. Bali, ni kuwa na upendo, urafiki na uhusiano mwema na watu wengine."', NULL);
