INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_10', 3, NULL, 'assets/topic/asb/25130.png', 'Punda sasa waliogopa sana. "Hebu tutoroke," mmoja wao alisema. Mwingine alijibu, "Hatuwezi. Fisi watatukimbiza."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_11', 3, NULL, 'assets/topic/asb/25131.png', 'Punda mzee alishauri, "Tumekuja kuonyesha heshima zetu kwenu. Tunasikitika kwamba mtoto wenu amefariki. Tutaomba Mungu awafariji. Sasa lazima turudi nyumbani kwetu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_12', 3, NULL, 'assets/topic/asb/25132.png', 'Fisi walisema, "Subiri! Hamuwezi kuondoka. Lazima mtupatie kitu cha kula. Tukatieni midomo yenu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22043_13', 3, NULL, 'assets/topic/asb/25133.png', 'Punda waliangaliana. "Pengine tukiwakatia midomo yetu, watatuachilia twende." Waliwaza.', NULL);
