INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_3', 3, NULL, 'assets/topic/asb/19860.png', 'Kadogo na Juma ni marafiki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_4', 3, NULL, 'assets/topic/asb/19861.png', 'Siku moja, Juma alisema, "Tukimbie kwa ajili ya bara letu, tuhimize umoja wa Afrika!"
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_5', 3, NULL, 'assets/topic/asb/19862.png', 'Waliamua kuibeba kurunzi ya umoja kwenye safari yao.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_6', 3, NULL, 'assets/topic/asb/19863.png', 'Kutoka Afrika Kusini, walielekea kaskazini kupitia magharibi mwa bara. Walipitia Namibia, Angola,  Kongo na Cameroon.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_7', 3, NULL, 'assets/topic/asb/19864.png', 'Wakimbiaji kutoka Afrika Magharibi walijiunga nao nchini Nigeria.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_8', 3, NULL, 'assets/topic/asb/19865.png', 'Walipofika Mali, walikumbwa na upepo mkali. Juma aliwashinda wote kwa nguvu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_9', 3, NULL, 'assets/topic/asb/19866.png', 'Walipofika pwani ya Guinea, jijini Conakry, walikuwa wamejaa vumbi. Walienda kuogelea baharini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_10', 3, NULL, 'assets/topic/asb/19867.png', 'Jijini Casablanca, wakimbiaji waliungana na vijana wenzao wakacheza ufukoni.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_11', 3, NULL, 'assets/topic/asb/19868.png', 'Walikimbia kupitia Algeria na Libya wakapumzika Misiri ili wazitembelee piramidi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_12', 3, NULL, 'assets/topic/asb/19869.png', 'Walipita kwenye jangwa na mbuga za kitaifa. Vijana zaidi walijiunga nao jijini Kampala.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_13', 3, NULL, 'assets/topic/asb/19870.png', 'Walipofika jijini Mombasa, waliketi ufukoni wakala wali wa nazi kwa samaki.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_14', 3, NULL, 'assets/topic/asb/19871.png', 'Hata hivyo, Juma alikuwa amechoka. Walipokuwa njiani wakielekea Tanzania, alianguka chini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_15', 3, NULL, 'assets/topic/asb/19872.png', 'Juma alimpokeza Kadogo kurunzi akisema, "Ibebe kurunzi hii hadi Mlimani Kilimanjaro. Hakikisha umewasha mwale wa umoja wa Afrika."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_16', 3, NULL, 'assets/topic/asb/19873.png', 'Watu waliwashangilia wakimbiaji wakisema, "Ninyi ni mashujaa wetu."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_17', 3, NULL, 'assets/topic/asb/19874.png', 'Walipofika Malawi, walikatiza mbio zao. Wakaenda kuogelea katika Ziwa Malawi.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19877_18', 3, NULL, 'assets/topic/asb/19875.png', 'Hatimaye, wakimbiaji wa umoja waliibeba kurunzi yao hadi nchini Zimbabwe. Umati wa watu ulikusanyika pale kushuhudia jambo hilo.
