INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635', 2, 'Mbu msumbufu', 'assets/topic/asb/14756.png', 'Ursula Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_3', 3, NULL, 'assets/topic/asb/14755.png', 'Nilisikia, "Hodi! Hodi!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_4', 3, NULL, 'assets/topic/asb/14756.png', '"Ni nani, jamani?" Niliuliza huku nikisinzia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_5', 3, NULL, 'assets/topic/asb/14757.png', '"Ni mimi." Sauti inayokwaruza sikio ilinijibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_6', 3, NULL, 'assets/topic/asb/14758.png', '"Nani ndiye ''Mimi''?" Niliuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_7', 3, NULL, 'assets/topic/asb/14759.png', '"Nilikwambia kuwa, ni-mi-mi tu." Sauti ile ile ilisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_8', 3, NULL, 'assets/topic/asb/14760.png', 'Niliajibu, "Mimi sijui mtu yeyote anayeitwa, ''ni-mi-mi tu.''" Nilitazama ikiwa chandarua changu kilikuwa kimenikinga vizuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_9', 3, NULL, 'assets/topic/asb/14761.png', '"Jina langu ni Amosi." Sauti iliyokwaruza zaidi kuliko awali ilisema. Ilisikika ikiwa karibu sana na sikio langu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_10', 3, NULL, 'assets/topic/asb/14762.png', '"Amosi nani?" Niliuliza huku nikitazama ndani ya chandarua.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_11', 3, NULL, 'assets/topic/asb/14763.png', '"Amosi Kuito." Hatimaye, sauti ilisema. Wakati huo huo niliumwa sikioni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_12', 3, NULL, 'assets/topic/asb/14764.png', '"Uui!" Niliruka juu na kuondoka kitandani. "Utalipia kitendo hiki!" Nilipiga kelele.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_13', 3, NULL, 'assets/topic/asb/14766.png', 'Kwa hasira, niliiasha taa kumtafuta adui wangu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('21635_14', 3, NULL, 'assets/topic/asb/14765.png', 'Nilitangaza vita dhidi ya Amosi Kuito msumbufu!', NULL);
