INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401', 2, 'Mbuni na Simba', 'assets/topic/asb/19205.png', 'Susan Kavaya
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_3', 3, NULL, 'assets/topic/asb/19195.png', 'Mbuni na Simba walikuwa marafiki sana. Wote wawili walikuwa na watoto wachanga.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_4', 3, NULL, 'assets/topic/asb/19196.png', 'Mbuni aliwalisha watoto wake vizuri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_5', 3, NULL, 'assets/topic/asb/19197.png', 'Watoto wa Simba hawakupata chakula cha kutosha.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_6', 3, NULL, 'assets/topic/asb/19198.png', 'Simba aliwaangalia vifaranga na kuwaza,  "Hawa ni wazuri! Natamani wangekuwa wangu!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_7', 3, NULL, 'assets/topic/asb/19199.png', 'Wakati Mbuni alikuwa hayupo,  Simba aliwachukua watoto wa Mbuni akaenda nao nyumbani kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_8', 3, NULL, 'assets/topic/asb/19200.png', 'Mbuni alirudi na kupata watoto wa Simba nyumbani kwake. Alilia,  "Watoto wangu wako wapi?"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_9', 3, NULL, 'assets/topic/asb/19201.png', 'Mbuni alikimbia nyumbani kwa Simba. Aliwapata wanawe wakiwa na Simba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_10', 3, NULL, 'assets/topic/asb/19202.png', 'Simba alikataa kuwarudisha watoto wa Mbuni. Alisema,  "Hawa ni wangu sasa. Unaweza kuwachukua watoto wa simba."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_11', 3, NULL, 'assets/topic/asb/19203.png', 'Mbuni alikasirika sana. "Nitafanyaje ili niwapate watoto wangu?" Alijiwazia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_12', 3, NULL, 'assets/topic/asb/19204.png', 'Halafu alifikiri,  "Ninajua nitakachokifanya. Nitawaita wanyama wote kwa mkutano."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_13', 3, NULL, 'assets/topic/asb/19205.png', 'Wanyama walikubali kukutana.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_14', 3, NULL, 'assets/topic/asb/19206.png', 'Tembo aliwauliza Mbuni na Simba kuzungumza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_15', 3, NULL, 'assets/topic/asb/19207.png', 'Wanyama wengi waliogopa kumlaumu Simba.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_16', 3, NULL, 'assets/topic/asb/19208.png', 'Mwishowe, Kuchakuro alisimama na kusema, "Watoto ambao wanafanana na ndege ni wa Mbuni. Watoto ambao wana mikia ni wa Simba jike."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_17', 3, NULL, 'assets/topic/asb/19209.png', 'Kuchakuro alitorokea tunduni. Wanyama wote walishangilia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('19401_18', 3, NULL, 'assets/topic/asb/19210.png', 'Simba alikasirika sana. Alienda nyumbani na watoto wake.', NULL);
