INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_3', 3, NULL, 'assets/topic/asb/6371.png', 'Mbuzi, Mbwa na Ng''ombe walikuwa marafiki wakubwa. Siku moja walisafiri kwa teksi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_4', 3, NULL, 'assets/topic/asb/6373.png', 'Walipofika mwisho wa safari yao, dereva aliwauliza walipe nauli zao. Ng''ombe akalipa.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_5', 3, NULL, 'assets/topic/asb/6374.png', 'Mbwa hakuwa na kiasi kamili cha pesa, akalipa zaidi kidogo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_6', 3, NULL, 'assets/topic/asb/6375.png', 'Dereva alipokuwa akimrudhishia Mbwa baki ya pesa zake, Mbuzi alitoroka bila kulipa chochote.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_7', 3, NULL, 'assets/topic/asb/6376.png', 'Dereva alikasirika sana. Akaondosha gari bila kumrudishia Mbwa baki ya pesa zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_8', 3, NULL, 'assets/topic/asb/6377.png', 'Hiyo ndiyo sababu, siku zote, Mbwa hulikimbilia gari kwa nia ya kumpata dereva arudishiwe baki ya pesa zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_9', 3, NULL, 'assets/topic/asb/6378.png', 'Mbuzi, anapousikia mlio wa gari, hutoroka. Yeye huogopa kushikwa kwa kukosa kulipa nauli.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7868_10', 3, NULL, 'assets/topic/asb/6379.png', 'Ng''ombe huwa hashughuliki gari linapokuja. Yeye huchukuwa muda kuvuka barabara kwa sababu alilipa nauli yake kamili!', NULL);
