INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059', 2, 'Mbuzi, mfalme bandia', 'assets/topic/asb/13362.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_3', 3, NULL, 'assets/topic/asb/13351.png', 'Hapo zamani, kulikuwa na mbuzi aliyeitwa Igobe.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_4', 3, NULL, 'assets/topic/asb/13350.png', '"Rafiki zangu, nimewaita kwa sababu niliota ndoto," Igobe alisema. Mifugo wote na ndege walimsikiliza mfalme wao kwa makini.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_5', 3, NULL, 'assets/topic/asb/13352.png', '"Niliota kuwa kulikuwa na njaa na ukosefu wa maji katika nchi yetu. Wengi wa jamaa zetu walifariki!" Mfalme alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_6', 3, NULL, 'assets/topic/asb/13353.png', 'Kuku na Bata walikuwa na wazo. Walisema, "Kila mmoja wetu alete chakula kiwekwe katika hifadhi ya mfalme."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_7', 3, NULL, 'assets/topic/asb/13362.png', 'Mfalme alitoa sharti moja. Alisema, "Yeyote atakayekosa kuleta chakula, mtamfunga mumlete kwangu."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_8', 3, NULL, 'assets/topic/asb/13354.png', 'Wakati wa mfalme Mbuzi kustaafu ulifika. Mifugo wote na ndege walikutana kumchagua mfalme mpya. Paka alichaguliwa kuwa mfalme wao mpya.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_9', 3, NULL, 'assets/topic/asb/13363.png', 'Mbuzi hakumtambua Paka kama mfalme. Alisema, "Mimi ndiye mfalme na hakuna mwingine. Siwezi kumtii mwingine yeyote."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_10', 3, NULL, 'assets/topic/asb/13355.png', 'Mifugo walipeleka chakula katika hifadhi ya mfalme wao mpya. Mbuzi hakupeleka chochote.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_11', 3, NULL, 'assets/topic/asb/13357.png', 'Mifugo wote na ndege walikasirika. "Kwani anadhani yeye ni tofauti nasi?" Walinung''unika.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_12', 3, NULL, 'assets/topic/asb/13356.png', 'Mbwa alisema, "Nilimsaidia alipokuwa mfalme. Sikuwa nikilala usiku. Kila wakati nilikuwa naye, tayari kumsaidia."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_13', 3, NULL, 'assets/topic/asb/13358.png', 'Kondoo alisema, "Nilimpatia sufu yangu ili awafunike wanawe."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_14', 3, NULL, 'assets/topic/asb/13359.png', 'Nguruwe naye alisema, "Alipokuwa mfalme, aliwaambia marafiki zake kuwa mimi ni mlafi. Lakini nilitumia muda mwingi kuitunza bustani yake na kupalilia mawele na mahindi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_15', 3, NULL, 'assets/topic/asb/13360.png', 'Kondoo aliuliza, "Eti alisema nini? Kwamba wewe ni mlafi? Alifikiri kuwa atakuwa mfalme milele?" Kondoo aliongeza kusema, "Lazima afahamu kuwa yeye si mfalme tena. Hata sijui kwa nini anafikiri kuwa yeye ni muhimu sana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_16', 3, NULL, 'assets/topic/asb/13361.png', 'Mifugo wote walicheka na kukubaliana kwamba lazima Mbuzi apeleke chakula kwa mfalme mpya. Mfalme Paka alimwamuru Mbuzi aende kwake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_17', 3, NULL, 'assets/topic/asb/13364.png', 'Mifugo walipoenda, walimkuta Mbuzi akistarehe. "Siendi kwa Paka. Hakuna mfalme mwingine ila mimi. Ukiwa mfalme, unasalia mflame." Mbuzi alisema kwa kiburi.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_18', 3, NULL, 'assets/topic/asb/13365.png', '"Basi tutakufunga kwa kamba tukupeleke kwa mfalme mpya," Kondoo alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_19', 3, NULL, 'assets/topic/asb/13366.png', 'Ng''ombe alimfunga Mbuzi akamvuta na kumpeleka kwa mfalme. Kondoo, Bata, Mbwa, Nguruwe na Jogoo walimshangilia Ng''ombe wakisema, "Mpeleke huyu mbuzi kaidi kwa mfalme mpya!"', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22059_20', 3, NULL, 'assets/topic/asb/13367.png', 'Tangu wakati huo, kila mbuzi hukataa kusonga anapovutwa. Hufikiri kuwa anapelekwa kwenye mahakama ya mfalme.', NULL);
