INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881', 2, 'Mbweha na Jua', 'assets/topic/asb/5253.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_3', 3, NULL, 'assets/topic/asb/5246.png', 'Hapo zamani, palikuwa na Mbweha mvivu pia mjinga. Yeye na babake mzee waliishi jangwani Kalahari.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_4', 3, NULL, 'assets/topic/asb/5247.png', 'Asubuhi moja, Mbweha mzee aliamka na kumpata mwanawe amelala juani. Chakula hakikuwa tayari na mbuzi walikuwa bado zizini! "Wewe kijana mvivu sana! Nenda ukamtafute mke. Nimezeeka na siwezi kukukimu tena," babake alisema. Mbweha aliruka juu na kuwapeleka mbuzi malishoni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_5', 3, NULL, 'assets/topic/asb/5250.png', 'Kichakani, aliona kitu kilichoangaza juu ya mwamba. Alipoukaribia mwamba ndipo mwangaza ulipozidi kupendeza. Labda huyu ndiye aliyekuwa mke wake!', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_6', 3, NULL, 'assets/topic/asb/5251.png', '"Wewe ni mzuri mno," Mbweha aliueleza mng''aro. "Lakini, wewe ni nani? Mbona uko peke yako?" "Mimi ni Jua," mng''aro ulijibu. "Familia yangu iliniacha hapa ilipohama. Haikutaka kunibeba maanake mimi ni moto sana."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_7', 3, NULL, 'assets/topic/asb/5252.png', 'Mbweha alisema, "Lakini, wewe u mzuri sana! Mimi nitakubeba. Nitakupeleka nyumbani nikujulishe kwa babangu." "Ni sawa, unaweza kunibeba, lakini,  usilalamike nikizidi kwa joto usiloweza kuhimili," Jua alisema.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_8', 3, NULL, 'assets/topic/asb/5253.png', 'Mbweha alienda nyumbani bila ngozi, manyoya mgongoni, wala bila mke.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_9', 3, NULL, 'assets/topic/asb/5255.png', 'Babake Mbweha aliuuguza mgongo wa mwanawe kwa kutumia mafuta.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_10', 3, NULL, 'assets/topic/asb/5256.png', 'Polepole, manyoya yaliota tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_11', 3, NULL, 'assets/topic/asb/5258.png', 'Manyoya hayo mapya yalikuwa rangi tofauti na yale mengine mwilini. Rangi zote tofauti zilimkumbusha Mbweha kila mara kutokuwa mjinga tena.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7881_12', 3, NULL, NULL, 'Maelezo kuhusu hadithi Katika mwaka wa 2005,  mwanahadithi wa Naro kwa jina la Bega Cgaze kutoka Jangwa la Kalahari nchini Botswana alimsimulia Marlene Winberg hadithi hii ambaye anaielezea hapa. Hadithi inapatikana katika mitindo tofauti katika jamii nyingi za kiSan za Afrika ya Kusini. Inawaonya vijana wengi wa kiume dhidi ya kudanganywa na urembo wa kimwili wa wasichana,  na inawaelezea wazazi kutowalazimisha watoto wao kuoa kwa ajili ya kupata msaada wa nyumbani. Vile vile inaonyesha nguvu za jua jangwani. Michoro imefanywa na Marlene na kufafanuliwa na Satsiri Winberg kupitia weldei wa kikundi cha Manyeka cha Kisanaa.', NULL);
