INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865', 2, 'Mbweha na zabibu chachu', 'assets/topic/asb/1775.png', 'Brigid Simiyu
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_3', 3, NULL, 'assets/topic/asb/1776.png', 'Hapo zamani za kale,  palikuwa na Mbweha aliyekuwa na njaa. Alitembea karibu na kichaka akitafuta chakula.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_4', 3, NULL, 'assets/topic/asb/1775.png', 'Aliuona mzabibu uliojaa zabibu mbivu. Kichala cha zabibu mbivu kilining''inia.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_5', 3, NULL, 'assets/topic/asb/1777.png', 'Mbweha aliwaza, "Zabibu hizi zinaonekana tamu kweli kweli." Alirudi nyuma hatua chache ili aweze kuzirukia zile zabibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_6', 3, NULL, 'assets/topic/asb/1778.png', 'Zabibu zilikuwa juu sana. Mbweha alipozirukia,  alianguka kwa mgongo wala hakuzifikia zile zabibu.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_7', 3, NULL, 'assets/topic/asb/1779.png', 'Aliinuka akurudi nyuma zaidi ili aweze kuzifikia zabibu. Mara hii alifika karibu zaidi, lakini akaanguka tena kwa kishindo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_8', 3, NULL, 'assets/topic/asb/1780.png', 'Alijaribu tena na tena, lakini hakuweza kuzifikia zabibu. Alianguka mara nying hadi mgongo ukamuuma.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_9', 3, NULL, 'assets/topic/asb/1781.png', 'Hatimaye, alikata tamaa na kwenda zake.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('7865_10', 3, NULL, 'assets/topic/asb/1782.png', 'Mbweha alijawa na majuto. Alisimama, akatazama nyuma na kuziangalia tena zile zabibu, "Zabibu hizo,  hata hivyo, zinaonekana chachu." Akajiliwaza. Aliondoka akaenda zake huku akihisi njaa kuliko hapo awali.', NULL);
