INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085', 2, 'Mchungaji', 'assets/topic/asb/19575.png', 'Brigid Simiyu', 'FFB3C8FF');
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_3', 3, NULL, 'assets/topic/asb/19569.png', 'Siku moja, Mchungaji hakuwa na nyasi za kuwalisha nguruwe, kondoo na mbuzi wake. Pia hakuwa na nafaka za kumlisha kuku. Vilevile hakuwa na zizi la kuwaweka mifgugo.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_4', 3, NULL, 'assets/topic/asb/19572.png', 'Aliamua kuwauza ili wapate chakula na makazi bora.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_5', 3, NULL, 'assets/topic/asb/19573.png', 'Alisema, "Ninaweza kuwauza kila mmoja robo."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_6', 3, NULL, 'assets/topic/asb/19574.png', 'Mnunuzi akasema, "Ninataka mbuzi peke yake."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_7', 3, NULL, 'assets/topic/asb/19575.png', 'Basi Mchungaji akawauza kondoo, mbuzi na nguruwe kwa robo kila mmoja. Kisha akampatia mnunuzi kuku bila malipo. Alipokuwa akirudi nyumbani Mchungaji alikuwa na huzuni.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_8', 3, NULL, 'assets/topic/asb/19577.png', 'Aligundua kwamba wale hawakuwa wanyama tu. Walikuwa marafiki zake pia.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_9', 3, NULL, 'assets/topic/asb/19578.png', '"Wanyama hawa sasa ni wangu," mnunuzi alisema. "Sitaweza kukurudishia."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_10', 3, NULL, 'assets/topic/asb/19581.png', '"Nimegundua kwamba wao si wanyama tu bali ni marafiki zangu," mchungaji alisema.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_11', 3, NULL, 'assets/topic/asb/19582.png', 'Jirani aliwasikia wakibishana akasema, "Nenda ujenge zizi, utafute nyasi na nafaka. Halafu, unaweza kuwarejesha wanyama wako."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('22085_12', 3, NULL, 'assets/topic/asb/19572.png', 'Mchungaji aliwapenda sana wanyama wake. Alimrudishia mnunuzi hela zake.
