INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750', 2, 'Mdiria na bahari', 'assets/topic/asb/26594.png', 'Ursula  Nafula
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_3', 3, NULL, 'assets/topic/asb/26584.png', 'Kuliishi mfalme katika Kisiwa cha Rusinga. Mfalme huyo hakuwa amehisi maumivu maishani mwake.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_4', 3, NULL, 'assets/topic/asb/26585.png', 'Asubuhi moja mfalme alikiinamisha kichwa chake. Aliwaza namna maumivu yanavyokuwa. "Kuna watu wengi maskini katika ufalme wangu. Nitawezaje kuhisi maumivu yao?" alijiuliza.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_5', 3, NULL, 'assets/topic/asb/26586.png', 'Mchana na usiku, alitamani kuhisi maumivu angalu mara moja tu.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_6', 3, NULL, 'assets/topic/asb/26587.png', 'Sauti hiyo iliendelea, "Yajenge mashua kubwa saba ambamo utaweka mali na familia yako. Kisha safiri ukielekea upande wa kaskazini mwa Kisiwa cha Rusinga."
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_7', 3, NULL, 'assets/topic/asb/26588.png', 'Muda mfupi baadaye, mashua saba yalijengwa na wakawa tayari kusafiri.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_8', 3, NULL, 'assets/topic/asb/26589.png', 'Siku moja kabla ya safari, mfalme aliandaa sherehe. Kila mmoja alihudhuria. Chui, nyoka, sungura, konokono, ndege na hata siafu, wote walifika kwa sherehe.', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_9', 3, NULL, 'assets/topic/asb/26590.png', 'Mfalme aliwahotubia watu, "Mimi na familia yangu tunaondoka kwenda sehemu tofauti ya Kisiwa cha Rusinga. Tunataka kuhisi maumivu ambayo watu wengine huhisi."', NULL);
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_10', 3, NULL, 'assets/topic/asb/26591.png', 'Watu walinong''onezana miongoni mwao. Wazee hawakumwamini.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_11', 3, NULL, 'assets/topic/asb/26592.png', 'Siku iliyofuata, mfalme na familia yake walisafiri.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_12', 3, NULL, 'assets/topic/asb/26593.png', 'Lakini siku ya tatu, bahari ilichafuka. Anga lilibadilika likatanda mawingu. Walikumbwa na dhoruba kubwa.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_13', 3, NULL, 'assets/topic/asb/26594.png', 'Mfalme pekee alinusurika. Lakini, alikuwa amebadilika na kuwa mdiria.
INSERT INTO `card` (id, type, title, header, content, option) VALUES ('24750_14', 3, NULL, 'assets/topic/asb/26595.png', 'Mdiria alipiga mbizi baharini mara nyingi. Hakuweza kuiokoa familia yake au hata baadhi ya mali yake.
